  2047                progressDiv.textContent = `Font "${fontName}" loaded!`;
  2048                setTimeout(() => progressDiv.remove(), 2000);
  2049
  2050                console.log('[SPLICE] Custom font loaded:', fontName);
  2051            } catch (err) {
