    if (ui.progressContainer) ui.progressContainer.classList.remove('hidden');
    if (ui.resultsEmpty) ui.resultsEmpty.classList.add('hidden');
    if (ui.combinedPreview) ui.combinedPreview.classList.add('hidden');
