function applyPreset(presetId) {
    const preset = PRESETS[presetId];
    if (!preset) return;

    // Custom preset - keep current settings
    if (presetId === 'custom' || !preset.settings) {
        saveSettings({ activePreset: 'custom' });
        setStatus('Using custom settings');
        return;
    }

