let previewSilences = [];
let previewTakes = [];
let currentChapters = [];
let currentZooms = [];
let selectedSilenceIndices = new Set();
let isOperationInProgress = false;
let lastDetectionResult = null;
let currentAudioPath = null;
let currentSequenceInfo = null;
let currentTranscript = null;

// Global state for captions/reframe modules
window.spliceState = {
    lastTranscript: null,
    currentVideoPath: null,
    activeSequence: null,
    audioPath: null
};

// Also expose transcript globally for music module
window.currentTranscript = null;

// ============================================================================
// INITIALIZATION
// ============================================================================
document.addEventListener('DOMContentLoaded', async () => {
    // Premium Loading Experience
    setTimeout(() => {
        const loader = document.getElementById('loading-overlay');
        if (loader) loader.classList.add('hidden');
    }, 800); // Small delay for smooth perception
    console.log('[SPLICE] Initializing CEP panel...');

    // Debug: Show init started
    const statusEl = document.getElementById('status');
    if (statusEl) statusEl.textContent = 'Initializing...';

    // Cache DOM elements
    cacheUIElements();

    // Load settings
    loadSettingsToUI();

    // Initialize UI handlers
    initOptionsToggle();
    initSliders();
    initFeatureToggles();
    initPresetSelector();
    initGoButton();
    initPreviewHandlers();
    initModals();
    initCredits();
    initHelpButton();

    // Initialize offline detection
    if (typeof initOfflineDetection === 'function') {
