/**
 * SPLICE CEP Panel Styles
 * Premium "Deep Space" Theme
 */

:root {
    /* Palette */
    --bg-core: #121212;
    --bg-panel: var(--bg-panel);
    --bg-card: rgba(30, 30, 30, 0.6);
    --glass-border: rgba(255, 255, 255, 0.08);
    --glass-highlight: rgba(255, 255, 255, 0.03);

    /* Accents */
    --accent-primary: var(--accent-primary);
    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);

    /* Functional Colors */
    --text-main: #f0f0f0;
    --text-muted: var(--text-muted)888;
    --success: #00e676;
    --error: #ff5252;

    /* Spacing & Radius */
    --radius-sm: 6px;
    --radius-md: 12px;
    --radius-lg: 16px;
    --spacing-xs: 4px;
    --spacing-sm: 8px;
    --spacing-md: 16px;

    /* Transitions */
    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
}

/* Reset & Base */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    background: var(--bg-core);
    color: var(--text-main);
    margin: 0;
    padding: var(--spacing-md);
    height: 100vh;
    overflow-y: auto;
    overflow-x: hidden;
    font-size: 12px;
    line-height: 1.5;
    -webkit-font-smoothing: antialiased;
}

/* Loading Overlay */
#loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--bg-core);
    z-index: 9999;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: opacity 0.5s var(--ease-smooth), visibility 0.5s;
}

#loading-overlay.hidden {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
}

.loader-logo {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background: var(--accent-gradient);
    animation: pulse-ring 2s infinite;
    margin-bottom: var(--spacing-md);
    box-shadow: var(--accent-glow);
}

.loader-text {
    font-size: 11px;
    color: var(--text-muted);
    letter-spacing: 1px;
    text-transform: uppercase;
    font-weight: 500;
}

@keyframes pulse-ring {
    0% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.7);
    }

    70% {
        transform: scale(1);
        box-shadow: 0 0 0 10px rgba(74, 158, 255, 0);
    }

    100% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0);
    }
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, 0.2);
}

/* Base UI Components */
button {
    font-family: inherit;
}

/* Header */
.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-md);
    padding-bottom: var(--spacing-sm);
    border-bottom: 1px solid var(--glass-border);
}

.header h1 {
    font-size: 16px;
    font-weight: 700;
    margin: 0;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    letter-spacing: -0.5px;
}

.header-actions {
    display: flex;
    gap: var(--spacing-sm);
    align-items: center;
}

/* Credit Badge */
.credit-badge {
    font-size: 10px;
    font-weight: 600;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 8px;
    padding: 4px 8px;
    cursor: pointer;
    transition: all 0.2s var(--ease-smooth);
}

.credit-badge:hover {
    border-color: var(--accent-primary);
}

.credit-badge.login {
    color: var(--accent-primary);
}

.credit-badge.ok {
    color: #4ade80;
}

.credit-badge.low {
    color: #f97316;
    border-color: #f97316;
}

.credit-badge.error {
    color: #ef4444;
    border-color: #ef4444;
}

.credit-badge.trial {
    color: #a855f7;
    background: rgba(168, 85, 247, 0.1);
    border-color: rgba(168, 85, 247, 0.3);
}

/* Action Card */
.action-card {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-md);
    padding: var(--spacing-md);
    margin-bottom: var(--spacing-md);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
    transition: transform 0.2s var(--ease-smooth);
}

.action-card h2 {
    font-size: 10px;
    font-weight: 700;
    margin: 0 0 var(--spacing-md) 0;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 1px;
}

/* GO Button */
.go-btn {
    width: 100%;
    padding: 16px;
    font-size: 14px;
    font-weight: 600;
    background: var(--accent-gradient);
    border: none;
    border-radius: var(--radius-md);
    color: white;
    cursor: pointer;
    margin-bottom: var(--spacing-sm);
    letter-spacing: 0.5px;
    transition: transform 0.2s var(--ease-smooth), box-shadow 0.2s;
    box-shadow: var(--accent-glow);
}

.go-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 15px rgba(74, 158, 255, 0.3);
}

.go-btn:active {
    transform: translateY(0);
}

.go-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

/* Icon Buttons */
.icon-btn {
    background: transparent;
    border: 1px solid var(--glass-border);
    color: var(--text-muted);
    width: 32px;
    height: 32px;
    border-radius: var(--radius-sm);
    cursor: pointer;
    font-size: 14px;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s var(--ease-smooth);
}

.icon-btn:hover {
    background: var(--glass-highlight);
    color: var(--text-main);
    border-color: var(--text-muted);
}

/* Options Toggle */
.options-toggle {
    background: transparent;
    border: none;
    color: var(--text-muted);
    font-size: 10px;
    cursor: pointer;
    padding: 4px 0;
    width: 100%;
    text-align: left;
}

.options-toggle:hover {
    color: #aaa;
}

.options-toggle::before {
    content: '+';
    margin-right: 4px;
    font-weight: bold;
}

.options-toggle.expanded::before {
    content: '-';
}

/* Options Panel */
.options-panel {
    margin-top: 8px;
    padding-top: 8px;
    border-top: 1px solid var(--glass-border);
}

.options-panel.collapsed,
.sub-options.collapsed {
    display: none;
}

/* Form Elements */
.form-group {
    margin-bottom: 10px;
}

.form-group label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 4px;
}

.form-group.small {
    margin-bottom: 6px;
}

.form-group.small label {
    font-size: 9px;
}

input[type="text"],
input[type="email"],
input[type="number"],
select {
    width: 100%;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 11px;
}

input[type="text"]:focus,
input[type="email"]:focus,
input[type="number"]:focus,
select:focus {
    outline: none;
    border-color: var(--accent-primary);
}

/* Slider */
.slider-container {
    margin-bottom: var(--spacing-md);
}

.slider-container label {
    display: block;
    font-size: 11px;
    color: var(--text-muted);
    margin-bottom: var(--spacing-xs);
    font-weight: 500;
}

.slider-container.small {
    margin-bottom: var(--spacing-sm);
}

input[type="range"] {
    width: 100%;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    outline: none;
    -webkit-appearance: none;
    appearance: none;
    /* Fixed lint: standard property */
    cursor: pointer;
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    /* Fixed lint */
    width: 16px;
    height: 16px;
    background: var(--accent-primary);
    border-radius: 50%;
    cursor: pointer;
    box-shadow: 0 0 10px rgba(74, 158, 255, 0.4);
    transition: transform 0.2s var(--ease-smooth);
    margin-top: -6px;
    /* Vertical centering adjustment */
}

input[type="range"]::-webkit-slider-thumb:hover {
    transform: scale(1.2);
}

input[type="range"]::-webkit-slider-runnable-track {
    width: 100%;
    height: 4px;
    cursor: pointer;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
}

.slider-labels {
    display: flex;
    justify-content: space-between;
    font-size: 10px;
    color: var(--text-muted);
    margin-top: var(--spacing-xs);
}

/* Checkbox */
.checkbox-label {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 11px;
    color: var(--text-main);
    cursor: pointer;
    margin-bottom: var(--spacing-sm);
    user-select: none;
}

.checkbox-label input[type="checkbox"] {
    width: 16px;
    height: 16px;
    accent-color: var(--accent-primary);
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    cursor: pointer;
}

/* Tier Badge */
.tier-badge {
    font-size: 9px;
    background: var(--accent-gradient);
    color: white;
    padding: 2px 6px;
    border-radius: var(--radius-sm);
    margin-left: var(--spacing-xs);
    font-weight: 600;
    box-shadow: var(--accent-glow);
}

/* Feature Toggles */
.feature-toggles {
    margin-top: 10px;
}

.sub-options {
    margin-left: 20px;
    margin-top: 6px;
    padding: 8px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
}

/* Results Area */
.results-area {
    background: var(--bg-panel);
    border-radius: 6px;
    padding: 12px;
    min-height: 60px;
}

.results-empty {
    text-align: center;
    color: #666;
    padding: 20px;
    font-size: 11px;
}

/* Progress */
.progress-container {
    text-align: center;
    padding: var(--spacing-md) 0;
}

.progress-container.hidden {
