}

.progress-bar-container {
    height: 6px;
    background: rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    overflow: hidden;
    margin-bottom: var(--spacing-sm);
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.3);
}

.progress-bar {
    height: 100%;
    background: var(--accent-gradient);
    transition: width 0.3s var(--ease-smooth);
    box-shadow: var(--accent-glow);
}

.progress-text {
    font-size: 11px;
    color: var(--text-muted);
    font-weight: 500;
}

/* Preview Section */
.preview-section {
    margin-top: 8px;
}

.preview-section.hidden {
    display: none;
}

.preview-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.preview-header h3 {
    font-size: 11px;
    font-weight: 600;
    color: #aaa;
    text-transform: uppercase;
}

.preview-counts {
    display: flex;
    gap: 10px;
    font-size: 10px;
    color: #666;
}

.preview-counts span span {
    color: var(--accent-primary);
    font-weight: 600;
}

.preview-controls {
    margin-bottom: 8px;
}

.preview-list {
    max-height: 200px;
    overflow-y: auto;
    margin-bottom: var(--spacing-md);
    padding-right: 4px;
    /* Space for scrollbar */
}

.preview-item {
    display: flex;
    align-items: center;
    padding: 8px 12px;
    margin-bottom: 6px;
    background: rgba(30, 30, 30, 0.4);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-sm);
    border-left: 3px solid var(--accent-primary);
    cursor: pointer;
    transition: all 0.15s var(--ease-smooth);
}

.preview-item:hover {
    background: rgba(255, 255, 255, 0.05);
    transform: translateX(2px);
}

.preview-item.excluded {
    opacity: 0.5;
    border-left-color: var(--text-muted);
    background: transparent;
}

.preview-item.take-item {
    border-left-color: var(--success);
}

.preview-item-check {
    min-width: 16px;
    min-height: 16px;
    margin-right: 10px;
    accent-color: var(--accent-primary);
}

.preview-item-icon {
    margin-right: 10px;
    font-size: 14px;
}

.preview-item-info {
    flex: 1;
    min-width: 0;
}

.preview-item-time {
    font-size: 11px;
    font-weight: 500;
    color: #e1e1e1;
}

.preview-item-duration,
.preview-item-label {
    font-size: 9px;
    color: var(--text-muted);
    margin-top: 2px;
}

.preview-actions {
    display: flex;
    gap: 8px;
}

/* Buttons */
.btn {
    padding: 8px 16px;
    font-size: 12px;
    font-weight: 500;
    border: none;
    border-radius: var(--radius-sm);
    cursor: pointer;
    transition: all 0.2s var(--ease-smooth);
}

.btn-primary {
    background: var(--accent-primary);
    color: white;
    box-shadow: 0 2px 8px rgba(74, 158, 255, 0.2);
}

.btn-primary:hover {
    background: #5aa8ff;
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(74, 158, 255, 0.3);
}

.btn-secondary {
    background: rgba(255, 255, 255, 0.1);
    color: var(--text-main);
}

.btn-secondary:hover {
    background: rgba(255, 255, 255, 0.15);
}

.btn-ghost {
    background: transparent;
    color: var(--text-muted);
    border: 1px solid var(--glass-border);
}

.btn-ghost:hover {
    background: var(--glass-highlight);
    color: var(--text-main);
    border-color: var(--text-muted);
}

.btn-small {
    padding: 6px 10px;
    font-size: 11px;
}

/* Chapter Results */
.chapter-results,
.zoom-results {
    margin-top: 12px;
    padding-top: 12px;
    border-top: 1px solid #333;
}

.chapter-results.hidden,
.zoom-results.hidden {
    display: none;
}

.chapter-header,
.zoom-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.chapter-header h3,
.zoom-header h3 {
    font-size: 11px;
    font-weight: 600;
    color: #aaa;
    text-transform: uppercase;
}

.chapter-actions {
    display: flex;
    gap: 4px;
}

.chapter-list,
.zoom-list {
    max-height: 120px;
    overflow-y: auto;
}

.chapter-item {
    display: flex;
    gap: 10px;
    padding: 6px 8px;
    background: var(--bg-card);
    border-radius: 4px;
    margin-bottom: 4px;
    cursor: pointer;
    transition: background 0.15s;
}

.chapter-item:hover {
    background: #333;
}

.chapter-time {
    font-size: 10px;
    color: var(--accent-primary);
    font-weight: 500;
    min-width: 50px;
}

.chapter-title {
    font-size: 10px;
    color: #e1e1e1;
    flex: 1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

/* Modal */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fadeIn 0.2s ease-out;
}

.modal.hidden {
    display: none;
}

.modal-content {
    background: var(--bg-panel);
