    border-radius: var(--radius-lg);
    width: 90%;
    max-width: 360px;
    max-height: 85%;
    overflow-y: auto;
    border: 1px solid var(--glass-border);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
    animation: slideUp 0.3s var(--ease-smooth);
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-md);
    border-bottom: 1px solid var(--glass-border);
    background: rgba(255, 255, 255, 0.02);
}

.modal-header h2 {
    font-size: 14px;
    font-weight: 600;
    margin: 0;
    color: var(--text-main);
}

.close-btn {
    background: transparent;
    border: none;
    color: var(--text-muted);
    font-size: 20px;
    cursor: pointer;
    padding: 4px;
    line-height: 1;
    border-radius: 50%;
    transition: all 0.2s;
    width: 28px;
    height: 28px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.close-btn:hover {
    color: var(--text-main);
    background: rgba(255, 255, 255, 0.1);
}

@keyframes fadeIn {
    from {
        opacity: 0;
    }

    to {
        opacity: 1;
    }
}

@keyframes slideUp {
    from {
        transform: translateY(20px);
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

/* Status Bar */
#status {
    padding: 8px 12px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-sm);
    font-size: 11px;
    margin-bottom: var(--spacing-md);
    color: var(--text-muted);
    display: flex;
    align-items: center;
}

/* Form Elements */
.form-group {
    margin-bottom: var(--spacing-md);
}

.form-group label {
    display: block;
    font-size: 11px;
    color: var(--text-muted);
    margin-bottom: var(--spacing-xs);
    font-weight: 500;
}

.form-group.small {
    margin-bottom: var(--spacing-sm);
}

input[type="text"],
input[type="email"],
input[type="number"],
select {
    width: 100%;
    padding: 10px 12px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-sm);
    color: var(--text-main);
    font-size: 12px;
    transition: all 0.2s var(--ease-smooth);
}

input[type="text"]:focus,
input[type="email"]:focus,
input[type="number"]:focus,
select:focus {
    outline: none;
    border-color: var(--accent-primary);
    background: rgba(0, 0, 0, 0.5);
    box-shadow: 0 0 0 2px rgba(74, 158, 255, 0.1);
}

.modal-body {
    padding: 16px;
}

.modal-body p {
    font-size: 11px;
    color: var(--text-muted);
    margin-bottom: 12px;
}

/* Login Modal Divider */
.login-divider {
    display: flex;
    align-items: center;
    text-align: center;
    margin: 16px 0;
}

.login-divider::before,
.login-divider::after {
    content: '';
    flex: 1;
    border-bottom: 1px solid var(--glass-border);
}

.login-divider span {
    font-size: 10px;
    color: var(--text-muted);
    padding: 0 12px;
}

/* Buy License Button */
.btn-buy-license {
    display: block;
    width: 100%;
    text-align: center;
    text-decoration: none;
    background: linear-gradient(135deg, #00D4FF, #0099CC);
    color: #000;
    font-weight: 600;
    padding: 10px 16px;
    border-radius: var(--radius-sm);
    transition: all 0.2s var(--ease-smooth);
}

.btn-buy-license:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(0, 212, 255, 0.3);
}

/* Multitrack Section */
.multitrack-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.section-toggle {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 10px 12px;
    cursor: pointer;
    font-size: 11px;
    font-weight: 500;
    color: #aaa;
}

.section-toggle:hover {
    color: #e1e1e1;
}

.toggle-icon {
    font-family: monospace;
    font-weight: bold;
    width: 12px;
}

.multitrack-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.multitrack-panel.collapsed {
    display: none;
}

/* Speaker Configuration */
.speaker-config {
    margin-bottom: 12px;
}

.speaker-config-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
    font-size: 10px;
    color: var(--text-muted);
}

.speaker-list {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.speaker-item {
    display: flex;
    gap: 6px;
    align-items: center;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    border-left: 3px solid var(--text-muted);
}

.speaker-item.speaker-0 {
    border-left-color: #ff6b6b;
}

.speaker-item.speaker-1 {
    border-left-color: #4ecdc4;
}

.speaker-item.speaker-2 {
    border-left-color: #ffe66d;
}

.speaker-item.speaker-3 {
    border-left-color: #95e1d3;
}

.speaker-name {
    flex: 1;
    min-width: 60px;
    padding: 4px 6px;
    background: #333;
    border: 1px solid var(--glass-border);
    border-radius: 3px;
    color: #e1e1e1;
    font-size: 10px;
}

.speaker-track {
    width: 45px;
    padding: 4px;
    background: #333;
    border: 1px solid var(--glass-border);
    border-radius: 3px;
    color: #e1e1e1;
    font-size: 10px;
}

.speaker-remove-btn {
    background: transparent;
    border: none;
    color: #666;
    font-size: 12px;
    cursor: pointer;
    padding: 2px 6px;
}

.speaker-remove-btn:hover {
    color: #dc3545;
}

/* Multitrack Parameters */
.multitrack-params {
    margin-bottom: 12px;
}

/* Multitrack Actions */
.multitrack-actions {
    display: flex;
    gap: 8px;
    margin-bottom: 12px;
}

.multitrack-actions .btn {
    flex: 1;
}

/* Distribution Chart */
.distribution-chart-container {
    margin-bottom: 10px;
}

.distribution-chart-container label {
    display: block;
    font-size: 9px;
    color: var(--text-muted);
    margin-bottom: 4px;
}

.distribution-chart {
    display: flex;
    height: 24px;
    background: var(--bg-panel);
    border-radius: 4px;
    overflow: hidden;
}

.distribution-bar {
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 9px;
    font-weight: 500;
    color: #fff;
    min-width: 0;
    transition: width 0.3s ease;
}

.distribution-bar.speaker-0 {
    background: #ff6b6b;
}

.distribution-bar.speaker-1 {
    background: #4ecdc4;
}

.distribution-bar.speaker-2 {
    background: #ffe66d;
    color: #333;
}

.distribution-bar.speaker-3 {
    background: #95e1d3;
    color: #333;
}

.distribution-bar.wide-shot {
    background: var(--text-muted);
}

/* Speaker Stats */
.speaker-stats {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
    margin-bottom: 10px;
}

.speaker-stat {
    display: flex;
    align-items: center;
    gap: 4px;
    font-size: 9px;
}

.speaker-stat-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
}

.speaker-stat-dot.speaker-0 {
    background: #ff6b6b;
}

.speaker-stat-dot.speaker-1 {
    background: #4ecdc4;
}

.speaker-stat-dot.speaker-2 {
    background: #ffe66d;
}

.speaker-stat-dot.speaker-3 {
    background: #95e1d3;
}

.speaker-stat-dot.wide-shot {
    background: var(--text-muted);
}

/* Decision List */
.decision-list-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 6px;
    font-size: 10px;
    color: var(--text-muted);
}

.decision-list {
    max-height: 150px;
    overflow-y: auto;
    margin-bottom: 10px;
}

.decision-item {
    display: flex;
    gap: 8px;
    align-items: center;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    font-size: 10px;
}

.decision-time {
    min-width: 70px;
    color: var(--accent-primary);
    font-weight: 500;
}

.decision-speaker {
    min-width: 60px;
    color: #e1e1e1;
}

.decision-speaker.wide-shot {
    color: var(--text-muted);
}

.decision-reason {
    flex: 1;
    color: #666;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.decision-seek {
    background: transparent;
    border: 1px solid var(--glass-border);
    color: var(--text-muted);
    width: 20px;
    height: 20px;
    border-radius: 3px;
    cursor: pointer;
    font-size: 10px;
}

.decision-seek:hover {
    background: #333;
    color: #e1e1e1;
}

/* Multitrack Preview Actions */
.multitrack-preview-actions {
    display: flex;
    justify-content: flex-end;
}

/* Animated Captions Section */
.captions-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.captions-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.captions-panel.collapsed {
    display: none;
}

/* Caption Templates */
.caption-templates {
    margin-bottom: 12px;
}

.caption-templates>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 6px;
}

.caption-template-gallery {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding: 4px 0;
}

.caption-template-card {
    min-width: 80px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    border: 2px solid transparent;
    cursor: pointer;
    transition: border-color 0.2s;
}

.caption-template-card:hover {
    border-color: #555;
}

.caption-template-card.selected {
    border-color: var(--accent-primary);
}

.template-preview {
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 3px;
    margin-bottom: 6px;
}

.template-preview-text {
    font-size: 9px;
    font-weight: bold;
}

.template-preview-mrbeast {
    background: #ff0000;
    color: #fff;
}

.template-preview-hormozi {
    background: #000;
    color: #ffeb3b;
}

.template-preview-gaming {
    background: linear-gradient(135deg, #9c27b0, #00bcd4);
    color: #fff;
    text-shadow: 0 0 5px #00bcd4;
}

.template-preview-corporate {
    background: #333;
    color: #fff;
}

.template-preview-karaoke {
    background: linear-gradient(90deg, var(--accent-primary) 50%, var(--bg-panel) 50%);
    color: #fff;
}

.template-info {
    display: flex;
    flex-direction: column;
    gap: 2px;
}

.template-name {
    font-size: 10px;
    font-weight: 500;
    color: #e1e1e1;
}

.template-desc {
    font-size: 8px;
    color: #666;
}

/* Caption Settings */
.caption-settings {
    margin-bottom: 12px;
}

/* Caption Actions */
.caption-actions {
    margin-bottom: 10px;
}

/* Caption Stats */
.caption-stats {
    display: flex;
    gap: 12px;
    margin-bottom: 10px;
    flex-wrap: wrap;
}

.caption-stats .stat-item {
    font-size: 10px;
    color: var(--text-muted);
}

/* Caption Status */
.caption-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.caption-status-info {
    color: var(--accent-primary);
}

.caption-status-success {
    color: #28a745;
}

.caption-status-error {
    color: #dc3545;
}

/* Caption Preview */
.caption-preview-container {
    margin-bottom: 10px;
}

.caption-preview-list {
    max-height: 150px;
    overflow-y: auto;
}

.caption-preview-item {
    display: flex;
    gap: 8px;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    font-size: 10px;
}

.caption-time {
    min-width: 40px;
    color: var(--accent-primary);
    font-weight: 500;
}

.caption-text {
    color: #e1e1e1;
    flex: 1;
}

.caption-keyword {
    color: #ffeb3b;
    font-weight: 600;
}

.caption-preview-more {
    text-align: center;
    font-size: 10px;
    color: #666;
    padding: 6px;
}

/* Caption Export Actions */
.caption-export-actions {
    display: flex;
    gap: 6px;
    flex-wrap: wrap;
}

.caption-export-actions.hidden {
    display: none;
}

/* Text Editor Section */
.text-editor-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.text-editor-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.text-editor-panel.collapsed {
    display: none;
}

/* Text Editor Bar */
.text-editor-bar {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 0;
    font-size: 10px;
    color: var(--text-muted);
}

.text-editor-word-count {
    color: #666;
}

.text-editor-dirty {
    color: #ffeb3b;
    font-weight: bold;
}

.text-editor-status {
    flex: 1;
    text-align: right;
}

.text-editor-status-info {
    color: var(--accent-primary);
}

.text-editor-status-success {
    color: #28a745;
}

.text-editor-status-error {
    color: #dc3545;
}

/* Text Editor Toolbar */
.text-editor-toolbar {
    display: flex;
    gap: 6px;
    align-items: center;
    padding: 6px 0;
    margin-bottom: 8px;
    border-bottom: 1px solid #333;
}

.toolbar-divider {
    width: 1px;
    height: 18px;
    background: var(--glass-border);
    margin: 0 4px;
}

.btn-icon {
    width: 28px;
    height: 28px;
    padding: 0;
    font-size: 14px;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Text Editor Content */
.text-editor-content-wrapper {
    margin-bottom: 10px;
}

.text-editor-content {
    width: 100%;
    height: 180px;
    padding: 10px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 11px;
    line-height: 1.5;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    resize: vertical;
}

.text-editor-content:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.text-editor-content:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.text-editor-content::placeholder {
    color: #666;
}

/* Search & Replace */
.text-editor-search {
    margin-bottom: 10px;
}

.search-row,
.replace-row {
    display: flex;
    gap: 6px;
    align-items: center;
    margin-bottom: 6px;
}

.search-input {
    flex: 1;
    min-width: 0;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 10px;
}

.search-input:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.search-results {
    font-size: 10px;
    color: #666;
    min-width: 35px;
    text-align: center;
}

/* Text Editor Preview */
.text-editor-preview {
    margin-bottom: 10px;
    min-height: 0;
}

.preview-empty {
    padding: 12px;
    text-align: center;
    font-size: 10px;
    color: #666;
    background: var(--bg-panel);
    border-radius: 4px;
}

.preview-summary {
    display: flex;
    gap: 12px;
    margin-bottom: 8px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.preview-stat {
    font-size: 10px;
    color: var(--text-muted);
}

.preview-changes {
    max-height: 120px;
    overflow-y: auto;
}

.preview-change {
    display: flex;
    gap: 8px;
    align-items: center;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    font-size: 10px;
    border-left: 3px solid #666;
}

.preview-change-deletion {
    border-left-color: #dc3545;
}

.preview-change-reorder {
    border-left-color: #ffeb3b;
}

.preview-change-insert {
    border-left-color: #28a745;
}

.change-type {
    min-width: 60px;
    text-transform: uppercase;
    font-size: 9px;
    font-weight: 600;
    color: var(--text-muted);
}

.change-desc {
    flex: 1;
    color: #e1e1e1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.change-time {
    color: var(--accent-primary);
    font-weight: 500;
}

/* Text Editor Actions */
.text-editor-actions {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
}

.text-editor-actions .btn {
    flex: 1;
    min-width: 100px;
}

.text-editor-actions .btn.ready {
    animation: pulse 1.5s infinite;
}

@keyframes pulse {

    0%,
    100% {
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.4);
    }

    50% {
        box-shadow: 0 0 0 8px rgba(74, 158, 255, 0);
    }
}

/* Social Reframe Section */
.social-reframe-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.social-reframe-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.social-reframe-panel.collapsed {
    display: none;
}

/* Platform Selector */
.reframe-platforms,
.reframe-aspects {
    margin-bottom: 10px;
}

.reframe-platforms>label,
.reframe-aspects>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 6px;
}

.reframe-platform-selector,
.reframe-aspect-selector {
    display: flex;
    gap: 6px;
    flex-wrap: wrap;
}

.reframe-platform-btn,
.reframe-aspect-btn {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 4px;
    padding: 8px 12px;
    background: var(--bg-panel);
    border: 2px solid transparent;
    border-radius: 4px;
    cursor: pointer;
    transition: border-color 0.2s, background 0.2s;
    min-width: 70px;
}

.reframe-platform-btn:hover,
.reframe-aspect-btn:hover {
    border-color: #555;
    background: var(--bg-card);
}

.reframe-platform-btn.selected,
.reframe-aspect-btn.selected {
    border-color: var(--accent-primary);
    background: var(--bg-card);
}

.platform-icon {
    width: 24px;
    height: 24px;
    background: #666;
    border-radius: 4px;
}

.platform-icon-tiktok {
    background: linear-gradient(135deg, #69C9D0, #EE1D52);
}

.platform-icon-reels {
