}

.platform-name,
.aspect-name {
    font-size: 9px;
    color: #aaa;
}

.aspect-preview {
    width: 30px;
    height: 24px;
    background: var(--glass-border);
    border-radius: 2px;
}

.aspect-preview-portrait {
    width: 14px;
    height: 24px;
}

.aspect-preview-square {
    width: 24px;
    height: 24px;
}

.aspect-preview-portrait_4_5 {
    width: 20px;
    height: 24px;
}

/* Reframe Settings */
.reframe-settings {
    margin-bottom: 10px;
}

/* Reframe Actions */
.reframe-actions {
    display: flex;
    gap: 8px;
    margin-bottom: 10px;
}

.reframe-actions .btn {
    flex: 1;
}

/* Reframe Status */
.reframe-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.reframe-status-info {
    color: var(--accent-primary);
}

.reframe-status-success {
    color: #28a745;
}

.reframe-status-error {
    color: #dc3545;
}

/* Analysis Results */
.reframe-analysis-results {
    margin-bottom: 10px;
}

.analysis-summary {
    display: flex;
    gap: 12px;
    margin-bottom: 10px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.analysis-stat {
    display: flex;
    flex-direction: column;
    gap: 2px;
}

.stat-label {
    font-size: 9px;
    color: #666;
}

.stat-value {
    font-size: 12px;
    font-weight: 600;
    color: #e1e1e1;
}

.analysis-formats {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.format-suggestion {
