            this.initialized[moduleName] = Date.now();
        },
        isInitialized: function(moduleName) {
            return !!this.initialized[moduleName];
        },
        getAll: function() {
            return Object.keys(this.initialized);
        },
        reset: function() {
            this.initialized = {};
        }
    };

