    ui.debugLogs.innerHTML = '<p class="debug-running">Running diagnostics... Please wait...</p>';

    try {
        // Test 0: Check CEP environment
        addDiagnosticLog('Test 0: Checking CEP environment...');
        if (window.__adobe_cep__) {
            addDiagnosticLog('‚úì CEP runtime detected (window.__adobe_cep__ exists)');
        } else {
            addDiagnosticLog('‚úó CEP runtime NOT detected - running in browser mode');
        }

        // Test 0.5: Basic ExtendScript test (doesn't require hostScript.jsx)
        addDiagnosticLog('Test 0.5: Testing basic ExtendScript...');
        try {
            const basicResult = await jsx.evalScript('1 + 1');
            addDiagnosticLog('‚úì Basic ExtendScript works: 1+1 = ' + basicResult);
        } catch (e) {
            addDiagnosticLog('‚úó Basic ExtendScript FAILED: ' + e.message);
            addDiagnosticLog('  ‚Üí This means Premiere Pro cannot run ANY ExtendScript');
            addDiagnosticLog('  ‚Üí Make sure CEP debug mode is enabled and Premiere was restarted');
        }

        // Test 0.6: Check if hostScript.jsx functions are loaded
        addDiagnosticLog('Test 0.6: Checking if hostScript.jsx is loaded...');
        try {
            const typeofGetVersion = await jsx.evalScript('typeof getVersion');
            if (typeofGetVersion === 'function') {
                addDiagnosticLog('‚úì hostScript.jsx is loaded (getVersion exists)');
            } else {
                addDiagnosticLog('‚úó hostScript.jsx NOT loaded (getVersion type: ' + typeofGetVersion + ')');
                addDiagnosticLog('  ‚Üí The JSX file may not be in the correct location');
            }
        } catch (e) {
            addDiagnosticLog('‚úó hostScript check FAILED: ' + e.message);
        }

        // Test 1: JSX Connection
        addDiagnosticLog('Test 1: Checking JSX connection...');
