	"name": "@roo-code/core",
	"description": "Platform agnostic core functionality for Roo Code.",
	"version": "0.0.0",
	"type": "module",
	"exports": "./src/index.ts",
	"scripts": {
		"lint": "eslint src --ext=ts --max-warnings=0",
		"check-types": "tsc --noEmit",
		"test": "vitest run",
		"clean": "rimraf .turbo"
