### 5.3 Usage Tracking API Route

```typescript
// /app/api/usage/track/route.ts
import { NextRequest, NextResponse } from 'next/server'
import { auth } from '@clerk/nextjs/server'
import { createClient } from '@supabase/supabase-js'
import Stripe from 'stripe'

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
)

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!)

export async function POST(req: NextRequest) {
  const { userId, orgId } = await auth()

  if (!userId) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  }

  const { usageType, quantity, metadata } = await req.json()

  try {
    // Get organization's Stripe customer ID
    const { data: org } = await supabase
      .from('organizations')
      .select('stripe_customer_id')
      .eq('clerk_org_id', orgId)
      .single()

    // Record usage in local database
    const now = new Date()
    const billingPeriodStart = new Date(now.getFullYear(), now.getMonth(), 1)
    const billingPeriodEnd = new Date(now.getFullYear(), now.getMonth() + 1, 0)

    const { data: usageRecord, error } = await supabase
      .from('usage_records')
      .insert({
        organization_id: orgId,
        user_id: userId,
        usage_type: usageType,
        quantity,
        metadata,
        billing_period_start: billingPeriodStart.toISOString(),
        billing_period_end: billingPeriodEnd.toISOString(),
      })
      .select()
      .single()

    if (error) throw error

    // Report usage to Stripe Meters API (2025 method)
    if (org?.stripe_customer_id) {
      await stripe.billing.meterEvents.create({
        event_name: usageType, // Must match meter configured in Stripe
        payload: {
          value: quantity.toString(),
          stripe_customer_id: org.stripe_customer_id,
        },
      })
    }

    return NextResponse.json({ success: true, usageRecord })
  } catch (error) {
    console.error('Usage tracking error:', error)
    return NextResponse.json(
      { error: 'Failed to track usage' },
      { status: 500 }
    )
  }
}
```

### 5.4 Get Usage Statistics

```typescript
// /app/api/usage/stats/route.ts
import { NextRequest, NextResponse } from 'next/server'
import { auth } from '@clerk/nextjs/server'
import { createClient } from '@supabase/supabase-js'

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
)

export async function GET(req: NextRequest) {
  const { orgId } = await auth()

  if (!orgId) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  }

  const now = new Date()
  const billingPeriodStart = new Date(now.getFullYear(), now.getMonth(), 1)

  try {
    // Get current month's usage
    const { data: currentUsage } = await supabase
      .from('usage_records')
      .select('usage_type, quantity')
      .eq('organization_id', orgId)
      .gte('recorded_at', billingPeriodStart.toISOString())

    // Get plan limits
    const { data: subscription } = await supabase
      .from('subscriptions')
      .select(`
        *,
        plan_limits (*)
      `)
      .eq('organization_id', orgId)
      .eq('status', 'active')
      .single()

    // Aggregate usage by type
    const usageByType = currentUsage?.reduce((acc, record) => {
      acc[record.usage_type] = (acc[record.usage_type] || 0) + record.quantity
      return acc
    }, {} as Record<string, number>) || {}

    // Get usage history for charts (last 30 days)
    const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000)

    const { data: usageHistory } = await supabase
      .from('usage_records')
      .select('usage_type, quantity, recorded_at')
      .eq('organization_id', orgId)
      .gte('recorded_at', thirtyDaysAgo.toISOString())
      .order('recorded_at', { ascending: true })

    return NextResponse.json({
      currentUsage: usageByType,
      limits: subscription?.plan_limits,
      history: usageHistory,
      billingPeriod: {
        start: billingPeriodStart,
        end: new Date(now.getFullYear(), now.getMonth() + 1, 0),
      },
    })
  } catch (error) {
    console.error('Error fetching usage stats:', error)
    return NextResponse.json(
      { error: 'Failed to fetch usage stats' },
      { status: 500 }
    )
  }
}
```

### 5.5 Stripe Webhook Handler

```typescript
// /app/api/webhooks/stripe/route.ts
import { NextRequest, NextResponse } from 'next/server'
import Stripe from 'stripe'
import { createClient } from '@supabase/supabase-js'

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!)
const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
)

export async function POST(req: NextRequest) {
  const body = await req.text()
  const sig = req.headers.get('stripe-signature')!

  let event: Stripe.Event

  try {
    event = stripe.webhooks.constructEvent(
      body,
      sig,
      process.env.STRIPE_WEBHOOK_SECRET!
    )
  } catch (err) {
    console.error('Webhook signature verification failed')
    return NextResponse.json({ error: 'Invalid signature' }, { status: 400 })
  }

  switch (event.type) {
    case 'customer.subscription.created':
    case 'customer.subscription.updated': {
      const subscription = event.data.object as Stripe.Subscription

      // Upsert subscription in database
      await supabase.from('subscriptions').upsert({
        stripe_subscription_id: subscription.id,
        stripe_price_id: subscription.items.data[0].price.id,
        status: subscription.status,
        current_period_start: new Date(subscription.current_period_start * 1000).toISOString(),
        current_period_end: new Date(subscription.current_period_end * 1000).toISOString(),
        cancel_at: subscription.cancel_at 
          ? new Date(subscription.cancel_at * 1000).toISOString() 
          : null,
      })

      // Record subscription history
      const eventType = event.type === 'customer.subscription.created' 
        ? 'created' 
        : 'updated'

      await supabase.from('subscription_history').insert({
        stripe_subscription_id: subscription.id,
        event_type: eventType,
        to_price_id: subscription.items.data[0].price.id,
        metadata: { stripe_event_id: event.id },
      })

      break
    }

    case 'customer.subscription.deleted': {
      const subscription = event.data.object as Stripe.Subscription

      await supabase
        .from('subscriptions')
        .update({ status: 'canceled' })
        .eq('stripe_subscription_id', subscription.id)

      await supabase.from('subscription_history').insert({
        stripe_subscription_id: subscription.id,
        event_type: 'canceled',
      })

      break
    }

    case 'invoice.paid': {
      const invoice = event.data.object as Stripe.Invoice
      // Handle successful payment - could send confirmation email, etc.
      console.log(`Invoice paid: ${invoice.id}`)
      break
    }

    case 'invoice.payment_failed': {
      const invoice = event.data.object as Stripe.Invoice
      // Handle failed payment - send notification, mark account, etc.
      console.log(`Payment failed for invoice: ${invoice.id}`)
      break
    }
  }

  return NextResponse.json({ received: true })
}
```

### 5.6 Dashboard Usage Component with Tremor

```tsx
// /app/dashboard/components/UsageOverview.tsx
'use client'

import { Card, AreaChart, ProgressBar, Metric, Text, Flex, Badge } from '@tremor/react'
import { useEffect, useState } from 'react'

interface UsageData {
  currentUsage: Record<string, number>
  limits: {
    monthly_minutes_limit: number
    max_api_keys: number
  }
  history: Array<{
    usage_type: string
    quantity: number
    recorded_at: string
  }>
  billingPeriod: {
    start: string
    end: string
  }
}

export function UsageOverview() {
  const [usage, setUsage] = useState<UsageData | null>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetch('/api/usage/stats')
      .then(res => res.json())
      .then(data => {
        setUsage(data)
        setLoading(false)
      })
      .catch(err => {
        console.error(err)
        setLoading(false)
      })
  }, [])

  if (loading) {
    return <div>Loading usage data...</div>
  }

  if (!usage) {
    return <div>Failed to load usage data</div>
  }

  const minutesUsed = usage.currentUsage.processing_minutes || 0
  const minutesLimit = usage.limits?.monthly_minutes_limit || 1000
  const usagePercentage = (minutesUsed / minutesLimit) * 100

  // Transform history for chart
  const chartData = usage.history.reduce((acc, record) => {
    const date = new Date(record.recorded_at).toLocaleDateString()
    const existing = acc.find(d => d.date === date)
    if (existing) {
      existing.minutes += record.quantity
    } else {
      acc.push({ date, minutes: record.quantity })
    }
    return acc
  }, [] as Array<{ date: string; minutes: number }>)

  return (
    <div className="space-y-6">
      {/* Usage Summary Card */}
      <Card>
        <Flex>
          <div>
            <Text>Minutes Used This Month</Text>
            <Metric>{minutesUsed.toFixed(1)} / {minutesLimit}</Metric>
          </div>
          <Badge color={usagePercentage > 90 ? 'red' : usagePercentage > 70 ? 'yellow' : 'green'}>
            {usagePercentage.toFixed(0)}% used
          </Badge>
        </Flex>
        <ProgressBar 
          value={usagePercentage} 
          color={usagePercentage > 90 ? 'red' : usagePercentage > 70 ? 'yellow' : 'blue'}
          className="mt-4"
        />
        <Text className="mt-2 text-gray-500">
          Billing period: {new Date(usage.billingPeriod.start).toLocaleDateString()} - {new Date(usage.billingPeriod.end).toLocaleDateString()}
        </Text>
      </Card>

      {/* Usage Chart */}
      <Card>
        <Text className="font-medium">Usage Over Time</Text>
        <AreaChart
          className="h-72 mt-4"
          data={chartData}
          index="date"
          categories={['minutes']}
          colors={['blue']}
          valueFormatter={(value) => `${value.toFixed(1)} min`}
        />
      </Card>
    </div>
  )
}
```

