    42→/**
    43→ * Initialize one-click silence removal workflow
    44→ */
    45→function initSilenceWorkflow() {
    46→  const goBtn = document.getElementById('silenceGoBtn');
    47→  if (!goBtn) return;
    48→
    49→  goBtn.addEventListener('click', async () => {
    50→    goBtn.disabled = true;
    51→
    52→    try {
    53→      // Show progress
    54→      showProgress('Preparing audio...');
    55→
    56→      // Step 1: Export audio (invisible to user)
    57→      const exportSuccess = await exportAudioInternal();
    58→      if (!exportSuccess) {
    59→        throw new Error('Audio export failed');
    60→      }
    61→
    62→      // Step 2: Get settings
    63→      const sensitivity = parseInt(document.getElementById('sensitivitySlider')?.value || 50);
    64→      const useOriginal = document.getElementById('sourceOriginal')?.checked ?? true;
    65→      const useIsolated = document.getElementById('sourceIsolated')?.checked ?? false;
    66→
    67→      // Step 3: Vocal isolation if needed
    68→      let audioPath = WAV_PATH;
    69→      if (useIsolated) {
    70→        showProgress('Isolating vocals (this may take a few minutes)...');
    71→        const isolateResult = await isolateVocals();
    72→        if (isolateResult.success) {
    73→          audioPath = isolateResult.outputPath;
    74→        } else {
    75→          throw new Error(isolateResult.error || 'Vocal isolation failed');
    76→        }
    77→      }
    78→
    79→      // Step 4: Detect silences
    80→      showProgress('Detecting silences...');
    81→      const params = mapSensitivity(sensitivity);
    82→      const silences = await detectSilences(audioPath, params);
    83→
    84→      if (silences.length === 0) {
    85→        showEmptyState();
    86→        setStatus('No silences detected. Try increasing sensitivity.');
    87→        goBtn.disabled = false;
    88→        return;
    89→      }
    90→
    91→      // Step 5: Show preview (instead of applying immediately)
    92→      showSilencePreview(silences);
    93→      setStatus(`Found ${silences.length} silence(s) - review and apply`);
    94→
    95→    } catch (err) {
    96→      showEmptyState();
    97→      setStatus('Error: ' + err.message);
    98→      console.error('[SPLICE] Silence workflow error:', err);
    99→    }
   100→
   101→    goBtn.disabled = false;
   102→  });
   103→}
