   105→/**
   106→ * Initialize one-click takes detection workflow
   107→ */
   108→function initTakesWorkflow() {
   109→  const goBtn = document.getElementById('takesGoBtn');
   110→  if (!goBtn) return;
   111→
   112→  goBtn.addEventListener('click', async () => {
   113→    goBtn.disabled = true;
   114→
   115→    try {
   116→      // Show progress
   117→      showProgress('Preparing audio...');
   118→
   119→      // Step 1: Export audio (invisible to user)
   120→      const exportSuccess = await exportAudioInternal();
   121→      if (!exportSuccess) {
   122→        throw new Error('Audio export failed');
   123→      }
   124→
   125→      // Step 2: Transcribe and analyze (combined endpoint)
   126→      showProgress('Transcribing and analyzing...');
   127→      const result = await transcribeAudio();
   128→
   129→      if (!result.success) {
   130→        throw new Error(result.error || 'Transcription failed');
   131→      }
   132→
   133→      // Extract takes from result
   134→      let takes = [];
   135→      if (result.takes && result.takes.takes) {
   136→        takes = result.takes.takes;
   137→      } else if (result.takes && Array.isArray(result.takes)) {
   138→        takes = result.takes;
   139→      }
   140→
   141→      if (takes.length === 0) {
   142→        showEmptyState();
   143→        setStatus('No takes detected in this audio');
   144→        goBtn.disabled = false;
   145→        return;
   146→      }
   147→
   148→      // Store for later use
   149→      window.currentTakes = takes;
   150→
   151→      // Step 3: Auto-mark best takes if enabled
   152→      const autoMark = document.getElementById('autoMarkBest')?.checked ?? true;
   153→      if (autoMark) {
   154→        // Mark AI-suggested takes as best
   155→        takes.forEach(take => {
   156→          if (take.isBest) {
   157→            take.userMarkedBest = true;
   158→          }
   159→        });
   160→      }
   161→
   162→      // Step 4: Show results
   163→      showTakesResults(takes, result.transcript);
   164→
   165→      setStatus(`Found ${takes.length} take(s)`);
   166→
   167→      // Refresh credits display after processing
   168→      refreshCredits();
   169→
   170→    } catch (err) {
   171→      showEmptyState();
   172→      setStatus('Error: ' + err.message);
   173→      console.error('[SPLICE] Takes workflow error:', err);
   174→    }
   175→
   176→    goBtn.disabled = false;
   177→  });
   178→}
