   363→async function isolateVocals() {
   364→  const response = await fetch(`${BACKEND_URL}/isolate-vocals`, {
   365→    method: 'POST',
   366→    headers: { 'Content-Type': 'application/json' },
   367→    body: JSON.stringify({ audioPath: WAV_PATH })
   368→  });
   369→
   370→  if (!response.ok) {
   371→    const err = await response.json();
   372→    return { success: false, error: err.error || 'Isolation request failed' };
   373→  }
   374→
   375→  return await response.json();
   376→}
