   378→/**
   379→ * Detect silences using backend
   380→ */
   381→async function detectSilences(audioPath, params) {
   382→  const response = await fetch(`${BACKEND_URL}/silences-audio`, {
   383→    method: 'POST',
   384→    headers: { 'Content-Type': 'application/json' },
   385→    body: JSON.stringify({
   386→      wavPath: audioPath,
   387→      threshold: params.dbThreshold,
   388→      minDuration: params.minDuration,
   389→      padding: params.padding
   390→    })
   391→  });
   392→
   393→  if (!response.ok) {
   394→    const err = await response.json();
   395→    throw new Error(err.error || 'Silence detection failed');
   396→  }
   397→
   398→  const data = await response.json();
   399→  if (!data.success) {
   400→    throw new Error(data.error || 'Silence detection failed');
   401→  }
   402→
   403→  // Store silences globally for razor workflow
   404→  currentSilences = data.silences;
   405→
   406→  return data.silences;
   407→}
