     1→/**
     2→ * Slice 7: Apply Takes to Timeline
     3→ *
     4→ * Timeline manipulation functions:
     5→ * - Label clips with take information
     6→ * - Delete non-best takes
     7→ *
     8→ * These are now callable functions used by main.js.
     9→ */
    10→
    11→/**
    12→ * Label clips on timeline with take information
    13→ * @returns {number} Number of clips labeled
    14→ */
    15→async function labelTakesOnTimeline() {
    16→  const takes = getCurrentTakes();
    17→
    18→  if (!takes || takes.length === 0) {
    19→    throw new Error('No takes to label');
    20→  }
    21→
    22→  const context = await getActiveSequence();
    23→  if (!context) {
    24→    throw new Error('No project or sequence open');
    25→  }
    26→
    27→  const { sequence } = context;
    28→
    29→  // Find clips and rename them based on which take they fall into
    30→  const videoTrackCount = await sequence.getVideoTrackCount();
    31→  let labeledCount = 0;
    32→
    33→  for (let i = 0; i < videoTrackCount; i++) {
    34→    const track = await sequence.getVideoTrack(i);
    35→    if (!track) continue;
    36→
    37→    const clips = await track.getTrackItems(ppro.Constants.TrackItemType.CLIP, false);
    38→    if (!clips) continue;
    39→
    40→    for (let j = 0; j < clips.length; j++) {
    41→      const clip = clips[j];
    42→      if (!clip) continue;
    43→
    44→      const clipStartObj = await clip.getStartTime();
    45→      const clipStart = clipStartObj.ticks !== undefined ? clipStartObj.ticks : clipStartObj;
    46→      const clipStartSec = clipStart / TICKS_PER_SECOND;
    47→
    48→      // Find which take this clip belongs to
    49→      for (const take of takes) {
    50→        const takeStart = take.start || take.startTime || 0;
    51→        const takeEnd = take.end || take.endTime || 0;
    52→
    53→        if (clipStartSec >= takeStart && clipStartSec < takeEnd) {
    54→          // Short label: "* Take 1 - description"
    55→          const isBest = take.isBest || take.userMarkedBest;
    56→          const prefix = isBest ? '* ' : '';
    57→          const takeNum = take.takeNumber || takes.indexOf(take) + 1;
    58→          const desc = take.shortLabel || take.description || take.text || '';
    59→          const shortDesc = desc.split(' ').slice(0, 4).join(' ');
    60→          const label = `${prefix}Take ${takeNum} - ${shortDesc}`;
    61→
    62→          // UXP action pattern
    63→          const project = await ppro.Project.getActiveProject();
    64→          const clipRef = clip;
    65→          const labelRef = label;
    66→
    67→          project.lockedAccess(() => {
    68→            project.executeTransaction((compoundAction) => {
    69→              const setNameAction = clipRef.createSetNameAction(labelRef);
    70→              compoundAction.addAction(setNameAction);
    71→            }, 'SPLICE: Label Clip');
    72→          });
    73→          labeledCount++;
    74→          break;
    75→        }
