     1→/**
     2→ * Settings Management
     3→ *
     4→ * Persists user preferences to localStorage
     5→ */
     6→
     7→const DEFAULT_SETTINGS = {
     8→  sensitivity: 50,
     9→  audioSource: 'original',
    10→  autoMarkBest: true,
    11→  rememberOptions: false,
    12→  silenceOptionsExpanded: false,
    13→  takesOptionsExpanded: false,
    14→  customerId: null  // Stripe customer ID for billing
    15→};
    16→
    17→let currentSettings = { ...DEFAULT_SETTINGS };
    18→
    19→/**
    20→ * Load settings from localStorage
    21→ */
    22→function loadSettings() {
    23→  try {
    24→    const saved = localStorage.getItem('spliceSettings');
    25→    if (saved) {
    26→      currentSettings = { ...DEFAULT_SETTINGS, ...JSON.parse(saved) };
    27→    }
    28→  } catch (e) {
    29→    console.warn('[SPLICE] Could not load settings:', e);
    30→    currentSettings = { ...DEFAULT_SETTINGS };
    31→  }
    32→  return currentSettings;
    33→}
    34→
    35→/**
    36→ * Save settings to localStorage
    37→ */
    38→function saveSettings(settings) {
    39→  try {
    40→    currentSettings = { ...currentSettings, ...settings };
    41→    localStorage.setItem('spliceSettings', JSON.stringify(currentSettings));
    42→  } catch (e) {
    43→    console.warn('[SPLICE] Could not save settings:', e);
    44→  }
    45→}
    46→
    47→/**
    48→ * Get current settings
    49→ */
    50→function getSettings() {
    51→  return { ...currentSettings };
    52→}
    53→
    54→/**
    55→ * Reset settings to defaults
    56→ */
    57→function resetSettings() {
    58→  currentSettings = { ...DEFAULT_SETTINGS };
    59→  try {
    60→    localStorage.removeItem('spliceSettings');
    61→  } catch (e) {
    62→    console.warn('[SPLICE] Could not clear settings:', e);
    63→  }
    64→}
    65→
    66→/**
    67→ * Initialize settings UI components
    68→ */
    69→function initSettingsUI() {
    70→  const settings = loadSettings();
    71→
    72→  // Apply saved sensitivity to slider
    73→  const sensitivitySlider = document.getElementById('sensitivitySlider');
    74→  if (sensitivitySlider) {
    75→    sensitivitySlider.value = settings.sensitivity;
    76→  }
    77→
    78→  // Apply saved audio source
    79→  const sourceOriginal = document.getElementById('sourceOriginal');
    80→  const sourceIsolated = document.getElementById('sourceIsolated');
