/**
 * SPLICE Plugin Main Entry Point
 *
 * Initializes all slice functionality when the plugin loads.
 */

// Initialize Premiere Pro API
const ppro = require('premierepro');

// Initialize all slices when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
  // Slice 1-2: Timeline reading
  initTimelineReader();

  // Slice 3: Audio export
  initAudioExporter();

  // Slice 6: Backend integration
  initAnalyzer();

  // Slice 7: Timeline manipulation
  initLabelTimeline();
  initApplyTakes();

  // Slice 8: Unified silence detection & removal
  initDetectRemove();

  // Slice 9: Razor (XML-based clip splitting)
  initSlice9();

  console.log('[SPLICE] Plugin initialized');
});

