     1/**
     2 * Slice 8: Unified Silence Detection & Removal
     3 *
     4 * Simplified 3-click workflow:
     5 * 1. SELECT - Choose Original or Isolated audio
     6 * 2. ADJUST - Set sensitivity slider
     7 * 3. EXECUTE - Click "Detect & Remove Silences"
     8 */
     9
    10// Store detected silences
    11let currentSilences = [];
    12
    13/**
    14 * Get current silences
    15 */
    16function getCurrentSilences() {
    17  return currentSilences;
    18}
    19
    20/**
    21 * Maps 0-100 sensitivity slider to detection parameters
    22 * Lower = keep more pauses, Higher = remove more aggressively
    23 */
    24function mapSensitivity(value) {
    25  const t = value / 100;
    26  return {
    27    dbThreshold: Math.round(-50 + (30 * t)),                    // -50dB  -20dB
    28    minDuration: parseFloat((2.0 - (1.7 * t)).toFixed(2)),      // 2.0s  0.3s
    29    padding: parseFloat((0.2 - (0.15 * t)).toFixed(2))          // 0.2s  0.05s
    30  };
    31}
    32
    33/**
    34 * Initialize the unified Detect & Remove button
    35 */
    36function initDetectRemove() {
    37  document.getElementById('detectRemoveBtn').addEventListener('click', async () => {
    38    const btn = document.getElementById('detectRemoveBtn');
    39    const resultsDiv = document.getElementById('silenceResults');
    40    const progressDiv = document.getElementById('isolationProgress');
    41
    42    const useOriginal = document.getElementById('sourceOriginal').checked;
    43    const useIsolated = document.getElementById('sourceIsolated').checked;
    44    const sensitivity = parseInt(document.getElementById('sensitivitySlider').value);
    45
    46    if (!useOriginal && !useIsolated) {
    47      setStatus('Please select at least one audio source');
    48      return;
    49    }
    50
    51    btn.disabled = true;
    52    btn.textContent = 'Processing...';
    53    resultsDiv.innerHTML = '';
    54
    55    try {
    56      const params = mapSensitivity(sensitivity);
    57      let audioPath = WAV_PATH;
    58
    59      // Step 1: Vocal isolation if selected (Creator+ only)
    60      if (useIsolated) {
    61        progressDiv.style.display = 'block';
    62        progressDiv.querySelector('span').textContent = 'Isolating vocals (this may take a few minutes)...';
    63
    64        const isolateResponse = await fetch(`${BACKEND_URL}/isolate-vocals`, {
    65          method: 'POST',
    66          headers: { 'Content-Type': 'application/json' },
    67          body: JSON.stringify({ audioPath: WAV_PATH })
    68        });
    69
    70        if (!isolateResponse.ok) {
    71          const err = await isolateResponse.json();
    72          throw new Error(err.error || 'Failed to isolate vocals');
    73        }
    74
    75        const isolateData = await isolateResponse.json();
    76        if (!isolateData.success) throw new Error(isolateData.error || 'Vocal isolation failed');
    77
    78        audioPath = isolateData.outputPath;
    79      }
    80
    81      // Step 2: Detect silences using FFprobe
    82      progressDiv.style.display = 'block';
    83      progressDiv.querySelector('span').textContent = 'Detecting silences...';
    84
    85      const detectResponse = await fetch(`${BACKEND_URL}/silences-audio`, {
    86        method: 'POST',
    87        headers: { 'Content-Type': 'application/json' },
    88        body: JSON.stringify({
    89          wavPath: audioPath,
    90          threshold: params.dbThreshold,
    91          minDuration: params.minDuration,
    92          padding: params.padding
    93        })
    94      });
    95
    96      if (!detectResponse.ok) {
    97        const err = await detectResponse.json();
    98        throw new Error(err.error || 'Failed to detect silences');
    99      }
   100
   101      const detectData = await detectResponse.json();
   102      if (!detectData.success) throw new Error(detectData.error || 'Silence detection failed');
   103
   104      currentSilences = detectData.silences;
   105
   106      // Display results
   107      resultsDiv.innerHTML = `
   108        <p><strong>${detectData.count}</strong> silence(s) found</p>
   109        <p style="color: #888;">Total: ${detectData.totalSilenceDuration}s of silence</p>
   110        <p style="color: #666; font-size: 10px;">
   111          ${params.dbThreshold}dB, ${params.minDuration}s min, ${params.padding}s padding
   112        </p>
   113      `;
   114
   115      // Step 3: Apply to timeline if silences found
   116      if (detectData.count > 0) {
   117        progressDiv.querySelector('span').textContent = 'Applying to timeline...';
   118
   119        const removedCount = await removeSilencesFromTimeline();
   120
   121        progressDiv.style.display = 'none';
   122
   123        if (removedCount > 0) {
   124          setStatus(`Disabled ${removedCount} clip(s) in ${detectData.count} silence region(s)`);
   125        } else {
   126          setStatus(`Found ${detectData.count} silences. Use Razor tool to split clips first.`);
   127        }
   128
   129        // Show razor section for advanced splitting
   130        showRazorSection();
   131      } else {
   132        progressDiv.style.display = 'none';
   133        setStatus('No silences detected. Try increasing sensitivity.');
   134      }
   135
   136    } catch (err) {
   137      progressDiv.style.display = 'none';
   138      resultsDiv.innerHTML = `<p style="color: #f66;">Error: ${err.message}</p>`;
   139      setStatus('Error: ' + err.message);
   140      console.error('SPLICE detection error:', err);
   141    } finally {
   142      btn.disabled = false;
   143      btn.textContent = 'Detect & Remove Silences';
   144    }
   145  });
   146}
   147
   148/**
   149 * Remove silences from timeline by disabling clips
   150 * Returns count of disabled clips
   151 */
   152async function removeSilencesFromTimeline() {
   153  if (!currentSilences || currentSilences.length === 0) {
   154    return 0;
   155  }
   156
   157  const context = await getActiveSequence();
   158  if (!context) {
   159    throw new Error('No project or sequence open');
   160  }
   161
   162  const { sequence } = context;
   163
   164  // Sort silences by start time DESCENDING (work from end to avoid time shifts)
   165  const sortedSilences = [...currentSilences].sort((a, b) => b.start - a.start);
   166
   167  let removedCount = 0;
   168  const videoTrackCount = await sequence.getVideoTrackCount();
   169  const audioTrackCount = await sequence.getAudioTrackCount();
   170
   171  for (const silence of sortedSilences) {
   172    const startTicks = Math.floor(silence.start * TICKS_PER_SECOND);
   173    const endTicks = Math.floor(silence.end * TICKS_PER_SECOND);
   174
   175    // Process video tracks
   176    for (let i = 0; i < videoTrackCount; i++) {
   177      const track = await sequence.getVideoTrack(i);
   178      if (!track) continue;
   179
   180      const clips = await track.getTrackItems(ppro.Constants.TrackItemType.CLIP, false);
   181      if (!clips) continue;
   182
   183      for (let j = clips.length - 1; j >= 0; j--) {
   184        const clip = clips[j];
   185        if (!clip) continue;
   186
   187        const clipStartObj = await clip.getStartTime();
   188        const clipEndObj = await clip.getEndTime();
   189        const clipStart = clipStartObj.ticks !== undefined ? clipStartObj.ticks : clipStartObj;
