    31→export interface AgentInput {
    32→  /**
    33→   * A short (3-5 word) description of the task
    34→   */
    35→  description: string;
    36→  /**
    37→   * The task for the agent to perform
    38→   */
    39→  prompt: string;
    40→  /**
    41→   * The type of specialized agent to use for this task
    42→   */
    43→  subagent_type: string;
    44→  /**
    45→   * Optional model to use for this agent. If not specified, inherits from parent. Prefer haiku for quick, straightforward tasks to minimize cost and latency.
    46→   */
    47→  model?: "sonnet" | "opus" | "haiku";
    48→  /**
    49→   * Optional agent ID to resume from. If provided, the agent will continue from the previous execution transcript.
    50→   */
    51→  resume?: string;
    52→  /**
    53→   * Set to true to run this agent in the background. Use TaskOutput to read the output later.
    54→   */
    55→  run_in_background?: boolean;
    56→}
    57→export interface BashInput {
    58→  /**
    59→   * The command to execute
    60→   */
    61→  command: string;
    62→  /**
    63→   * Optional timeout in milliseconds (max 600000)
    64→   */
    65→  timeout?: number;
    66→  /**
    67→   * Clear, concise description of what this command does in 5-10 words, in active voice. Examples:
    68→   * Input: ls
    69→   * Output: List files in current directory
    70→   *
    71→   * Input: git status
    72→   * Output: Show working tree status
    73→   *
    74→   * Input: npm install
    75→   * Output: Install package dependencies
    76→   *
    77→   * Input: mkdir foo
    78→   * Output: Create directory 'foo'
    79→   */
    80→  description?: string;
    81→  /**
    82→   * Set to true to run this command in the background. Use TaskOutput to read the output later.
    83→   */
    84→  run_in_background?: boolean;
    85→  /**
    86→   * Set this to true to dangerously override sandbox mode and run commands without sandboxing.
    87→   */
    88→  dangerouslyDisableSandbox?: boolean;
    89→}
