   149→export interface GlobInput {
   150→  /**
   151→   * The glob pattern to match files against
   152→   */
   153→  pattern: string;
   154→  /**
   155→   * The directory to search in. If not specified, the current working directory will be used. IMPORTANT: Omit this field to use the default directory. DO NOT enter "undefined" or "null" - simply omit it for the default behavior. Must be a valid directory path if provided.
   156→   */
   157→  path?: string;
   158→}
   159→export interface GrepInput {
   160→  /**
   161→   * The regular expression pattern to search for in file contents
   162→   */
   163→  pattern: string;
   164→  /**
   165→   * File or directory to search in (rg PATH). Defaults to current working directory.
   166→   */
   167→  path?: string;
   168→  /**
   169→   * Glob pattern to filter files (e.g. "*.js", "*.{ts,tsx}") - maps to rg --glob
   170→   */
   171→  glob?: string;
   172→  /**
   173→   * Output mode: "content" shows matching lines (supports -A/-B/-C context, -n line numbers, head_limit), "files_with_matches" shows file paths (supports head_limit), "count" shows match counts (supports head_limit). Defaults to "files_with_matches".
   174→   */
   175→  output_mode?: "content" | "files_with_matches" | "count";
   176→  /**
   177→   * Number of lines to show before each match (rg -B). Requires output_mode: "content", ignored otherwise.
   178→   */
   179→  "-B"?: number;
   180→  /**
   181→   * Number of lines to show after each match (rg -A). Requires output_mode: "content", ignored otherwise.
   182→   */
   183→  "-A"?: number;
   184→  /**
   185→   * Number of lines to show before and after each match (rg -C). Requires output_mode: "content", ignored otherwise.
   186→   */
   187→  "-C"?: number;
   188→  /**
   189→   * Show line numbers in output (rg -n). Requires output_mode: "content", ignored otherwise. Defaults to true.
   190→   */
   191→  "-n"?: boolean;
   192→  /**
   193→   * Case insensitive search (rg -i)
   194→   */
   195→  "-i"?: boolean;
   196→  /**
   197→   * File type to search (rg --type). Common types: js, py, rust, go, java, etc. More efficient than include for standard file types.
   198→   */
   199→  type?: string;
   200→  /**
   201→   * Limit output to first N lines/entries, equivalent to "| head -N". Works across all output modes: content (limits output lines), files_with_matches (limits file paths), count (limits count entries). Defaults to 0 (unlimited).
   202→   */
   203→  head_limit?: number;
   204→  /**
   205→   * Skip first N lines/entries before applying head_limit, equivalent to "| tail -n +N | head -N". Works across all output modes. Defaults to 0.
   206→   */
   207→  offset?: number;
   208→  /**
   209→   * Enable multiline mode where . matches newlines and patterns can span lines (rg -U --multiline-dotall). Default: false.
   210→   */
   211→  multiline?: boolean;
   212→}
