{
	"name": "multi-agent",
	"displayName": "%extension.displayName%",
	"description": "%extension.description%",
	"publisher": "imorgado",
	"version": "0.1.0",
	"icon": "assets/icons/icon.png",
	"galleryBanner": {
		"color": "#617A91",
		"theme": "dark"
	},
	"engines": {
		"vscode": "^1.84.0",
		"node": "20.19.2"
	},
	"author": {
		"name": "MultiAgent"
	},
	"repository": {
		"type": "git",
		"url": "https://github.com/imorgado/multi-agent"
	},
	"homepage": "https://github.com/imorgado/multi-agent",
	"categories": [
		"AI",
		"Chat",
		"Programming Languages",
		"Education",
		"Snippets",
		"Testing"
	],
	"keywords": [
		"multi-agent",
		"multiagent",
		"parallel",
		"claude",
		"gemini",
		"mcp",
		"openrouter",
		"coding",
		"agent",
		"autonomous",
		"ai"
	],
	"activationEvents": [
		"onLanguage",
		"onStartupFinished"
	],
	"main": "./dist/extension.js",
	"contributes": {
		"viewsContainers": {
			"activitybar": [
				{
					"id": "multi-agent-ActivityBar",
					"title": "%views.activitybar.title%",
					"icon": "assets/icons/icon.svg"
				}
			]
		},
		"views": {
			"multi-agent-ActivityBar": [
				{
					"type": "webview",
					"id": "multi-agent.SidebarProvider",
					"name": "%views.sidebar.name%"
				}
			]
		},
		"commands": [
			{
				"command": "multi-agent.plusButtonClicked",
				"title": "%command.newTask.title%",
				"icon": "$(edit)"
			},
			{
				"command": "multi-agent.historyButtonClicked",
				"title": "%command.history.title%",
				"icon": "$(history)"
			},
			{
				"command": "multi-agent.marketplaceButtonClicked",
				"title": "%command.marketplace.title%",
				"icon": "$(extensions)"
			},
			{
				"command": "multi-agent.popoutButtonClicked",
				"title": "%command.openInEditor.title%",
				"icon": "$(link-external)"
			},
			{
				"command": "multi-agent.cloudButtonClicked",
				"title": "%command.cloud.title%",
				"icon": "$(cloud)"
			},
			{
				"command": "multi-agent.settingsButtonClicked",
				"title": "%command.settings.title%",
				"icon": "$(settings-gear)"
			},
			{
				"command": "multi-agent.openInNewTab",
				"title": "%command.openInNewTab.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.explainCode",
				"title": "%command.explainCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.fixCode",
				"title": "%command.fixCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.improveCode",
				"title": "%command.improveCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.addToContext",
				"title": "%command.addToContext.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.newTask",
				"title": "%command.newTask.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.terminalAddToContext",
				"title": "%command.terminal.addToContext.title%",
				"category": "Terminal"
			},
			{
				"command": "multi-agent.terminalFixCommand",
				"title": "%command.terminal.fixCommand.title%",
				"category": "Terminal"
			},
			{
				"command": "multi-agent.terminalExplainCommand",
				"title": "%command.terminal.explainCommand.title%",
				"category": "Terminal"
			},
			{
				"command": "multi-agent.setCustomStoragePath",
				"title": "%command.setCustomStoragePath.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.importSettings",
				"title": "%command.importSettings.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.focusInput",
				"title": "%command.focusInput.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.acceptInput",
				"title": "%command.acceptInput.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "multi-agent.toggleAutoApprove",
				"title": "%command.toggleAutoApprove.title%",
				"category": "%configuration.title%"
			}
		],
		"menus": {
			"editor/context": [
				{
					"submenu": "multi-agent.contextMenu",
					"group": "1"
				}
			],
			"multi-agent.contextMenu": [
				{
					"command": "multi-agent.addToContext",
					"group": "1_actions@1"
				},
				{
					"command": "multi-agent.explainCode",
					"group": "1_actions@2"
				},
				{
					"command": "multi-agent.improveCode",
					"group": "1_actions@3"
				}
			],
			"terminal/context": [
				{
					"submenu": "multi-agent.terminalMenu",
					"group": "2"
				}
			],
			"multi-agent.terminalMenu": [
				{
					"command": "multi-agent.terminalAddToContext",
					"group": "1_actions@1"
				},
				{
					"command": "multi-agent.terminalFixCommand",
					"group": "1_actions@2"
				},
				{
					"command": "multi-agent.terminalExplainCommand",
					"group": "1_actions@3"
				}
			],
			"view/title": [
				{
					"command": "multi-agent.plusButtonClicked",
					"group": "navigation@1",
					"when": "view == multi-agent.SidebarProvider"
				},
				{
					"command": "multi-agent.settingsButtonClicked",
					"group": "navigation@2",
					"when": "view == multi-agent.SidebarProvider"
				},
				{
					"command": "multi-agent.cloudButtonClicked",
					"group": "navigation@3",
					"when": "view == multi-agent.SidebarProvider"
				},
				{
					"command": "multi-agent.marketplaceButtonClicked",
					"group": "navigation@4",
					"when": "view == multi-agent.SidebarProvider"
				},
				{
					"command": "multi-agent.historyButtonClicked",
					"group": "overflow@1",
					"when": "view == multi-agent.SidebarProvider"
				},
				{
					"command": "multi-agent.popoutButtonClicked",
					"group": "overflow@2",
					"when": "view == multi-agent.SidebarProvider"
				}
			],
			"editor/title": [
				{
					"command": "multi-agent.plusButtonClicked",
					"group": "navigation@1",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				},
				{
					"command": "multi-agent.settingsButtonClicked",
					"group": "navigation@2",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				},
				{
					"command": "multi-agent.cloudButtonClicked",
					"group": "navigation@3",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				},
				{
					"command": "multi-agent.marketplaceButtonClicked",
					"group": "navigation@4",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				},
				{
					"command": "multi-agent.historyButtonClicked",
					"group": "overflow@1",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				},
				{
					"command": "multi-agent.popoutButtonClicked",
					"group": "overflow@2",
					"when": "activeWebviewPanelId == multi-agent.TabPanelProvider"
				}
			]
		},
		"keybindings": [
			{
				"command": "multi-agent.addToContext",
				"key": "cmd+k cmd+a",
				"mac": "cmd+k cmd+a",
				"win": "ctrl+k ctrl+a",
				"linux": "ctrl+k ctrl+a",
				"when": "editorTextFocus && editorHasSelection"
			},
			{
				"command": "multi-agent.toggleAutoApprove",
				"key": "cmd+alt+a",
				"mac": "cmd+alt+a",
				"win": "ctrl+alt+a",
				"linux": "ctrl+alt+a"
			}
		],
		"submenus": [
			{
				"id": "multi-agent.contextMenu",
				"label": "%views.contextMenu.label%"
			},
			{
				"id": "multi-agent.terminalMenu",
				"label": "%views.terminalMenu.label%"
			}
		],
		"configuration": {
			"title": "%configuration.title%",
			"properties": {
				"multi-agent.allowedCommands": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [
						"git log",
						"git diff",
						"git show"
					],
					"description": "%commands.allowedCommands.description%"
				},
				"multi-agent.deniedCommands": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [],
					"description": "%commands.deniedCommands.description%"
				},
				"multi-agent.commandExecutionTimeout": {
					"type": "number",
					"default": 0,
					"minimum": 0,
					"maximum": 600,
					"description": "%commands.commandExecutionTimeout.description%"
				},
				"multi-agent.commandTimeoutAllowlist": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [],
					"description": "%commands.commandTimeoutAllowlist.description%"
				},
				"multi-agent.preventCompletionWithOpenTodos": {
					"type": "boolean",
					"default": false,
					"description": "%commands.preventCompletionWithOpenTodos.description%"
				},
				"multi-agent.vsCodeLmModelSelector": {
					"type": "object",
					"properties": {
						"vendor": {
							"type": "string",
							"description": "%settings.vsCodeLmModelSelector.vendor.description%"
						},
						"family": {
							"type": "string",
							"description": "%settings.vsCodeLmModelSelector.family.description%"
						}
					},
					"description": "%settings.vsCodeLmModelSelector.description%"
				},
				"multi-agent.customStoragePath": {
					"type": "string",
					"default": "",
					"description": "%settings.customStoragePath.description%"
				},
				"multi-agent.enableCodeActions": {
					"type": "boolean",
					"default": true,
					"description": "%settings.enableCodeActions.description%"
				},
				"multi-agent.autoImportSettingsPath": {
					"type": "string",
					"default": "",
					"description": "%settings.autoImportSettingsPath.description%"
				},
				"multi-agent.maximumIndexedFilesForFileSearch": {
					"type": "number",
					"default": 10000,
					"minimum": 5000,
					"maximum": 500000,
					"description": "%settings.maximumIndexedFilesForFileSearch.description%"
				},
				"multi-agent.useAgentRules": {
					"type": "boolean",
					"default": true,
					"description": "%settings.useAgentRules.description%"
				},
				"multi-agent.apiRequestTimeout": {
					"type": "number",
					"default": 600,
					"minimum": 0,
					"maximum": 3600,
					"description": "%settings.apiRequestTimeout.description%"
				},
				"multi-agent.newTaskRequireTodos": {
					"type": "boolean",
					"default": false,
					"description": "%settings.newTaskRequireTodos.description%"
				},
				"multi-agent.codeIndex.embeddingBatchSize": {
					"type": "number",
					"default": 60,
					"minimum": 1,
					"maximum": 200,
					"description": "%settings.codeIndex.embeddingBatchSize.description%"
				},
				"multi-agent.debug": {
					"type": "boolean",
					"default": false,
					"description": "%settings.debug.description%"
				},
				"multi-agent.bundledMcp.docker.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.bundledMcp.docker.enabled.description%"
				},
				"multi-agent.bundledMcp.github.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.bundledMcp.github.enabled.description%"
				},
				"multi-agent.bundledMcp.railway.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.bundledMcp.railway.enabled.description%"
				},
				"multi-agent.bundledMcp.stripe.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.bundledMcp.stripe.enabled.description%"
				},
				"multi-agent.automations.enabled": {
					"type": "boolean",
					"default": true,
					"description": "%settings.automations.enabled.description%"
				},
				"multi-agent.automations.configPath": {
					"type": "string",
					"default": "",
					"description": "%settings.automations.configPath.description%"
				},
				"multi-agent.automations.autoApproveDefault": {
					"type": "boolean",
					"default": false,
					"description": "%settings.automations.autoApproveDefault.description%"
				},
				"multi-agent.browser.previewMode": {
					"type": "string",
					"enum": ["simple-browser", "puppeteer", "external"],
					"default": "simple-browser",
					"enumDescriptions": [
						"%settings.browser.previewMode.simpleBrowser.description%",
						"%settings.browser.previewMode.puppeteer.description%",
						"%settings.browser.previewMode.external.description%"
					],
					"description": "%settings.browser.previewMode.description%"
				},
				"multi-agent.browser.autoOpenPreview": {
					"type": "boolean",
					"default": true,
					"description": "%settings.browser.autoOpenPreview.description%"
				},
				"multi-agent.browserBridge.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.browserBridge.enabled.description%"
				},
				"multi-agent.browserBridge.port": {
					"type": "number",
					"default": 9876,
					"minimum": 1024,
					"maximum": 65535,
					"description": "%settings.browserBridge.port.description%"
				},
				"multi-agent.browserBridge.autoConnect": {
					"type": "boolean",
					"default": true,
					"description": "%settings.browserBridge.autoConnect.description%"
				},
				"multi-agent.google.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.google.enabled.description%"
				},
				"multi-agent.google.useOAuth": {
					"type": "boolean",
					"default": false,
					"description": "%settings.google.useOAuth.description%"
				},
				"multi-agent.bundledMcp.claudeInChrome.enabled": {
					"type": "boolean",
					"default": false,
					"description": "%settings.bundledMcp.claudeInChrome.enabled.description%"
				}
			}
		}
	},
	"scripts": {
		"lint": "eslint . --ext=ts --max-warnings=0",
		"check-types": "tsc --noEmit",
		"pretest": "turbo run bundle --cwd ..",
		"test": "vitest run",
		"format": "prettier --write .",
		"bundle": "node esbuild.mjs",
		"vscode:prepublish": "pnpm bundle --production",
		"vsix": "mkdirp ../bin && vsce package --no-dependencies --out ../bin",
		"publish:marketplace": "vsce publish --no-dependencies && ovsx publish --no-dependencies",
		"watch:bundle": "pnpm bundle --watch",
		"watch:tsc": "cd .. && tsc --noEmit --watch --project src/tsconfig.json",
		"clean": "rimraf README.md CHANGELOG.md LICENSE dist logs mock .turbo"
	},
	"dependencies": {
		"@anthropic-ai/bedrock-sdk": "^0.10.2",
		"@anthropic-ai/sdk": "^0.37.0",
		"@anthropic-ai/vertex-sdk": "^0.7.0",
		"@aws-sdk/client-bedrock-runtime": "^3.922.0",
		"@aws-sdk/credential-providers": "^3.922.0",
		"@google/genai": "^1.29.1",
		"@lmstudio/sdk": "^1.1.1",
		"@mistralai/mistralai": "^1.9.18",
		"@modelcontextprotocol/sdk": "1.12.0",
		"@qdrant/js-client-rest": "^1.14.0",
		"@multi-agent/cloud": "workspace:^",
		"@multi-agent/core": "workspace:^",
		"@multi-agent/ipc": "workspace:^",
		"@multi-agent/telemetry": "workspace:^",
		"@multi-agent/types": "workspace:^",
		"@vscode/codicons": "^0.0.36",
		"async-mutex": "^0.5.0",
		"axios": "^1.12.0",
		"cheerio": "^1.0.0",
		"chokidar": "^4.0.1",
		"clone-deep": "^4.0.1",
		"default-shell": "^2.2.0",
		"delay": "^6.0.0",
		"diff": "^5.2.0",
		"diff-match-patch": "^1.0.5",
		"exceljs": "^4.4.0",
		"fast-deep-equal": "^3.1.3",
		"fast-xml-parser": "^5.0.0",
		"fastest-levenshtein": "^1.0.16",
		"fzf": "^0.5.2",
		"get-folder-size": "^5.0.0",
		"google-auth-library": "^9.15.1",
		"gray-matter": "^4.0.3",
		"i18next": "^25.0.0",
		"ignore": "^7.0.3",
		"isbinaryfile": "^5.0.2",
		"jwt-decode": "^4.0.0",
		"lodash.debounce": "^4.0.8",
		"mammoth": "^1.9.1",
		"monaco-vscode-textmate-theme-converter": "^0.1.7",
		"node-cache": "^5.1.2",
		"node-ipc": "^12.0.0",
		"ollama": "^0.5.17",
		"openai": "^5.12.2",
		"os-name": "^6.0.0",
		"p-limit": "^6.2.0",
		"p-wait-for": "^5.0.2",
		"partial-json": "^0.1.7",
		"pdf-parse": "^1.1.1",
		"pkce-challenge": "^5.0.0",
		"pretty-bytes": "^7.0.0",
		"proper-lockfile": "^4.1.2",
		"ps-tree": "^1.2.0",
		"puppeteer-chromium-resolver": "^24.0.0",
		"puppeteer-core": "^23.4.0",
		"reconnecting-eventsource": "^1.6.4",
		"safe-stable-stringify": "^2.5.0",
		"sanitize-filename": "^1.6.3",
		"say": "^0.16.0",
		"semver-compare": "^1.0.0",
		"serialize-error": "^12.0.0",
		"shell-quote": "^1.8.2",
		"simple-git": "^3.27.0",
		"socket.io-client": "^4.8.1",
		"sound-play": "^1.1.0",
		"stream-json": "^1.8.0",
		"string-similarity": "^4.0.4",
		"strip-ansi": "^7.1.0",
		"strip-bom": "^5.0.0",
		"tiktoken": "^1.0.21",
		"tmp": "^0.2.3",
		"tree-sitter-wasms": "^0.1.12",
		"turndown": "^7.2.0",
		"uuid": "^11.1.0",
		"vscode-material-icons": "^0.1.1",
		"web-tree-sitter": "^0.25.6",
		"workerpool": "^9.2.0",
		"node-cron": "^3.0.3",
		"ws": "^8.18.0",
		"yaml": "^2.8.0",
		"zod": "3.25.61"
	},
	"devDependencies": {
		"@multi-agent/build": "workspace:^",
		"@multi-agent/config-eslint": "workspace:^",
		"@multi-agent/config-typescript": "workspace:^",
		"@types/clone-deep": "^4.0.4",
		"@types/debug": "^4.1.12",
		"@types/diff": "^5.2.1",
		"@types/diff-match-patch": "^1.0.36",
		"@types/glob": "^8.1.0",
		"@types/lodash.debounce": "^4.0.9",
		"@types/mocha": "^10.0.10",
		"@types/node": "20.x",
		"@types/node-cron": "^3.0.11",
		"@types/node-cache": "^4.1.3",
		"@types/node-ipc": "^9.2.3",
		"@types/proper-lockfile": "^4.1.4",
		"@types/ps-tree": "^1.1.6",
		"@types/semver-compare": "^1.0.3",
		"@types/shell-quote": "^1.7.5",
		"@types/stream-json": "^1.7.8",
		"@types/string-similarity": "^4.0.2",
		"@types/tmp": "^0.2.6",
		"@types/turndown": "^5.0.5",
		"@types/vscode": "^1.84.0",
		"@types/ws": "^8.5.14",
		"@vscode/test-electron": "^2.5.2",
		"@vscode/vsce": "3.3.2",
		"esbuild-wasm": "^0.25.0",
		"execa": "^9.5.2",
		"glob": "^11.1.0",
		"mkdirp": "^3.0.1",
		"nock": "^14.0.4",
		"npm-run-all2": "^8.0.1",
		"ovsx": "0.10.4",
		"rimraf": "^6.0.1",
		"tsup": "^8.4.0",
		"tsx": "^4.19.3",
		"typescript": "5.8.3",
		"vitest": "^3.2.3",
		"zod-to-ts": "^1.2.0"
	}
}

import * as vscode from "vscode"
import * as dotenvx from "@dotenvx/dotenvx"
import * as path from "path"

// Load environment variables from .env file
try {
	// Specify path to .env file in the project root directory
	const envPath = path.join(__dirname, "..", ".env")
	dotenvx.config({ path: envPath })
} catch (e) {
	// Silently handle environment loading errors
	console.warn("Failed to load environment variables:", e)
}

import type { CloudUserInfo, AuthState } from "@multi-agent/types"
import { CloudService, BridgeOrchestrator } from "@multi-agent/cloud"
import { TelemetryService, PostHogTelemetryClient } from "@multi-agent/telemetry"
import { customToolRegistry } from "@multi-agent/core"

import "./utils/path" // Necessary to have access to String.prototype.toPosix.
import { createOutputChannelLogger, createDualLogger } from "./utils/outputChannelLogger"

import { Package } from "./shared/package"
import { formatLanguage } from "./shared/language"
import { ContextProxy } from "./core/config/ContextProxy"
import { ClineProvider } from "./core/webview/ClineProvider"
import { DIFF_VIEW_URI_SCHEME } from "./integrations/editor/DiffViewProvider"
import { TerminalRegistry } from "./integrations/terminal/TerminalRegistry"
import { claudeCodeOAuthManager } from "./integrations/claude-code/oauth"
import { googleOAuthManager } from "./integrations/google"
import { McpServerManager } from "./services/mcp/McpServerManager"
import { BundledMcpManager } from "./services/mcp/bundled"
