// Initialize browser extension bridge for Chrome companion extension
const browserBridgeEnabled = vscode.workspace.getConfiguration(Package.name).get<boolean>("browserBridge.enabled", false)
if (browserBridgeEnabled) {
	const browserBridge = BrowserExtensionBridge.getInstance(context, {
		port: vscode.workspace.getConfiguration(Package.name).get<number>("browserBridge.port", 9876),
	})
	browserBridge.start().then(() => {
		outputChannel.appendLine(`[BrowserExtensionBridge] Server started on ${browserBridge.getServerUrl()}`)
	}).catch((error) => {
		outputChannel.appendLine(`[BrowserExtensionBridge] Failed to start: ${error instanceof Error ? error.message : String(error)}`)
	})
	browserBridge.on("connected", (info) => {
		outputChannel.appendLine(`[BrowserExtensionBridge] Connected to ${info.browserName} v${info.browserVersion}`)
	})
	browserBridge.on("disconnected", () => {
		outputChannel.appendLine("[BrowserExtensionBridge] Disconnected from browser")
	})
	context.subscriptions.push(browserBridge)
}

// Initialize workspace manager for multi-project support
const workspaceManager = WorkspaceManager.getInstance({
	extensionContext: context,
	outputChannel,
})
await workspaceManager.initialize()
context.subscriptions.push(workspaceManager)

// Initialize the provider *before* the MultiAgent Cloud service.
const provider = new ClineProvider(context, outputChannel, "sidebar", contextProxy, mdmService)

// Initialize MultiAgent Cloud service.
const postStateListener = () => ClineProvider.getVisibleInstance()?.postStateToWebview()

authStateChangedHandler = async (data: { state: AuthState; previousState: AuthState }) => {
	postStateListener()

	if (data.state === "logged-out") {
		try {
			await provider.remoteControlEnabled(false)
		} catch (error) {
			cloudLogger(
				`[authStateChangedHandler] remoteControlEnabled(false) failed: ${error instanceof Error ? error.message : String(error)}`,
			)
		}
	}

	// Handle Roo models cache based on auth state (ROO-202)
	const handleRooModelsCache = async () => {
		try {
			if (data.state === "active-session") {
				// Refresh with auth token to get authenticated models
				const sessionToken = CloudService.hasInstance()
					? CloudService.instance.authService?.getSessionToken()
					: undefined
				await refreshModels({
					provider: "roo",
					baseUrl: process.env.ROO_CODE_PROVIDER_URL ?? "https://api.roocode.com/proxy",
					apiKey: sessionToken,
				})
			} else {
				// Flush without refresh on logout
				await flushModels({ provider: "roo" }, false)
			}
		} catch (error) {
			cloudLogger(
				`[authStateChangedHandler] Failed to handle Roo models cache: ${error instanceof Error ? error.message : String(error)}`,
			)
		}
	}

	if (data.state === "active-session" || data.state === "logged-out") {
		await handleRooModelsCache()

		// Apply stored provider model to API configuration if present
		if (data.state === "active-session") {
			try {
				const storedModel = context.globalState.get<string>("roo-provider-model")
				if (storedModel) {
					cloudLogger(`[authStateChangedHandler] Applying stored provider model: ${storedModel}`)
					// Get the current API configuration name
					const currentConfigName =
						provider.contextProxy.getGlobalState("currentApiConfigName") || "default"
					// Update it with the stored model using upsertProviderProfile
					await provider.upsertProviderProfile(currentConfigName, {
						apiProvider: "roo",
						apiModelId: storedModel,
					})
					// Clear the stored model after applying
					await context.globalState.update("roo-provider-model", undefined)
					cloudLogger(`[authStateChangedHandler] Applied and cleared stored provider model`)
				}
			} catch (error) {
				cloudLogger(
					`[authStateChangedHandler] Failed to apply stored provider model: ${error instanceof Error ? error.message : String(error)}`,
				)
			}
		}
	}
}

settingsUpdatedHandler = async () => {
	const userInfo = CloudService.instance.getUserInfo()

	if (userInfo && CloudService.instance.cloudAPI) {
		try {
			provider.remoteControlEnabled(CloudService.instance.isTaskSyncEnabled())
		} catch (error) {
			cloudLogger(
				`[settingsUpdatedHandler] remoteControlEnabled failed: ${error instanceof Error ? error.message : String(error)}`,
			)
		}
	}

	postStateListener()
}

userInfoHandler = async ({ userInfo }: { userInfo: CloudUserInfo }) => {
	postStateListener()

	if (!CloudService.instance.cloudAPI) {
		cloudLogger("[userInfoHandler] CloudAPI is not initialized")
		return
	}

	try {
		provider.remoteControlEnabled(CloudService.instance.isTaskSyncEnabled())
	} catch (error) {
		cloudLogger(
			`[userInfoHandler] remoteControlEnabled failed: ${error instanceof Error ? error.message : String(error)}`,
		)
	}
}

cloudService = await CloudService.createInstance(context, cloudLogger, {
	"auth-state-changed": authStateChangedHandler,
	"settings-updated": settingsUpdatedHandler,
	"user-info": userInfoHandler,
})

try {
	if (cloudService.telemetryClient) {
		TelemetryService.instance.register(cloudService.telemetryClient)
	}
} catch (error) {
	outputChannel.appendLine(
		`[CloudService] Failed to register TelemetryClient: ${error instanceof Error ? error.message : String(error)}`,
	)
}

	// Add to subscriptions for proper cleanup on deactivate.
	context.subscriptions.push(cloudService)

	// Trigger initial cloud profile sync now that CloudService is ready.
	try {
		await provider.initializeCloudProfileSyncWhenReady()
	} catch (error) {
		outputChannel.appendLine(
			`[CloudService] Failed to initialize cloud profile sync: ${error instanceof Error ? error.message : String(error)}`,
		)
	}

	// Finish initializing the provider.
	TelemetryService.instance.setProvider(provider)

	context.subscriptions.push(
		vscode.window.registerWebviewViewProvider(ClineProvider.sideBarId, provider, {
			webviewOptions: { retainContextWhenHidden: true },
		}),
	)

	// Initialize automation manager for workflow automations
	const automationsEnabled = vscode.workspace.getConfiguration(Package.name).get<boolean>("automations.enabled", true)
	const firstWorkspacePath = vscode.workspace.workspaceFolders?.[0]?.uri.fsPath

	if (firstWorkspacePath) {
		automationManager = new AutomationManager({
			workspacePath: firstWorkspacePath,
			taskProvider: provider,
			enabled: automationsEnabled,
		})

		// Initialize in background
		automationManager.initialize().catch((error) => {
			outputChannel.appendLine(
				`[AutomationManager] Error during initialization: ${error instanceof Error ? error.message : String(error)}`,
			)
		})

		// Listen for automation events
		automationManager.onAutomationTriggered(({ automation, payload }) => {
			outputChannel.appendLine(`[AutomationManager] Automation triggered: ${automation.name} (${automation.id})`)
		})

		automationManager.onAutomationExecuted((result) => {
			outputChannel.appendLine(
				`[AutomationManager] Automation executed: ${result.automationId} - ${result.success ? "success" : "failed"}${result.error ? `: ${result.error}` : ""}`,
			)
		})

		// Listen for configuration changes
		context.subscriptions.push(
			vscode.workspace.onDidChangeConfiguration((e) => {
				if (e.affectsConfiguration(`${Package.name}.automations.enabled`) && automationManager) {
					const enabled = vscode.workspace.getConfiguration(Package.name).get<boolean>("automations.enabled", true)
					automationManager.setEnabled(enabled)
				}
			}),
		)

		// Add to disposables
		context.subscriptions.push({
			dispose: async () => {
				if (automationManager) {
					await automationManager.dispose()
				}
			},
		})

		outputChannel.appendLine(`[AutomationManager] Initialized for workspace: ${firstWorkspacePath}`)
	}

	// Auto-import configuration if specified in settings.
	try {
		await autoImportSettings(outputChannel, {
			providerSettingsManager: provider.providerSettingsManager,
			contextProxy: provider.contextProxy,
			customModesManager: provider.customModesManager,
		})
	} catch (error) {
		outputChannel.appendLine(
			`[AutoImport] Error during auto-import: ${error instanceof Error ? error.message : String(error)}`,
		)
	}

	registerCommands({ context, outputChannel, provider })

	/**
	 * We use the text document content provider API to show the left side for diff
	 * view by creating a virtual document for the original content. This makes it
	 * readonly so users know to edit the right side if they want to keep their changes.
	 *
	 * This API allows you to create readonly documents in VSCode from arbitrary
	 * sources, and works by claiming an uri-scheme for which your provider then
	 * returns text contents. The scheme must be provided when registering a
	 * provider and cannot change afterwards.
	 *
	 * Note how the provider doesn't create uris for virtual documents - its role
	 * is to provide contents given such an uri. In return, content providers are
	 * wired into the open document logic so that providers are always considered.
	 *
	 * https://code.visualstudio.com/api/extension-guides/virtual-documents
	 */
	const diffContentProvider = new (class implements vscode.TextDocumentContentProvider {
		provideTextDocumentContent(uri: vscode.Uri): string {
			return Buffer.from(uri.query, "base64").toString("utf-8")
		}
	})()

	context.subscriptions.push(
		vscode.workspace.registerTextDocumentContentProvider(DIFF_VIEW_URI_SCHEME, diffContentProvider),
	)

	context.subscriptions.push(vscode.window.registerUriHandler({ handleUri }))

	// Register code actions provider.
	context.subscriptions.push(
		vscode.languages.registerCodeActionsProvider({ pattern: "**/*" }, new CodeActionProvider(), {
			providedCodeActionKinds: CodeActionProvider.providedCodeActionKinds,
		}),
	)

	registerCodeActions(context)
	registerTerminalActions(context)

	// Allows other extensions to activate once Roo is ready.
	vscode.commands.executeCommand(`${Package.name}.activationCompleted`)

	// Implements the `MultiAgentAPI` interface.
	const socketPath = process.env.ROO_CODE_IPC_SOCKET_PATH
	const enableLogging = typeof socketPath === "string"

	// Watch the core files and automatically reload the extension host.
	if (process.env.NODE_ENV === "development") {
		const watchPaths = [
			{ path: context.extensionPath, pattern: "**/*.ts" },
			{ path: path.join(context.extensionPath, "../packages/types"), pattern: "**/*.ts" },
			{ path: path.join(context.extensionPath, "../packages/telemetry"), pattern: "**/*.ts" },
			{ path: path.join(context.extensionPath, "node_modules/@multi-agent/cloud"), pattern: "**/*" },
		]

		console.log(
			` Core auto-reloading: Watching for changes in ${watchPaths.map(({ path }) => path).join(", ")}`,
		)

		// Create a debounced reload function to prevent excessive reloads
		let reloadTimeout: NodeJS.Timeout | undefined
		const DEBOUNCE_DELAY = 1_000

		const debouncedReload = (uri: vscode.Uri) => {
			if (reloadTimeout) {
				clearTimeout(reloadTimeout)
			}

			console.log(` ${uri.fsPath} changed; scheduling reload...`)

			reloadTimeout = setTimeout(() => {
				console.log(` Reloading host after debounce delay...`)
				vscode.commands.executeCommand("workbench.action.reloadWindow")
			}, DEBOUNCE_DELAY)
		}

		watchPaths.forEach(({ path: watchPath, pattern }) => {
			const relPattern = new vscode.RelativePattern(vscode.Uri.file(watchPath), pattern)
			const watcher = vscode.workspace.createFileSystemWatcher(relPattern, false, false, false)

			// Listen to all change types to ensure symlinked file updates trigger reloads.
			watcher.onDidChange(debouncedReload)
			watcher.onDidCreate(debouncedReload)
			watcher.onDidDelete(debouncedReload)

			context.subscriptions.push(watcher)
		})

		// Clean up the timeout on deactivation
		context.subscriptions.push({
			dispose: () => {
				if (reloadTimeout) {
					clearTimeout(reloadTimeout)
				}
			},
		})
	}

	// Initialize background model cache refresh
	initializeModelCacheRefresh()

	return new API(outputChannel, provider, socketPath, enableLogging)
}

// This method is called when your extension is deactivated.
export async function deactivate() {
	outputChannel.appendLine(`${Package.name} extension deactivated`)

	if (cloudService && CloudService.hasInstance()) {
		try {
			if (authStateChangedHandler) {
				CloudService.instance.off("auth-state-changed", authStateChangedHandler)
			}

import * as vscode from "vscode"
import delay from "delay"

import type { CommandId } from "@multi-agent/types"
import { TelemetryService } from "@multi-agent/telemetry"

import { Package } from "../shared/package"
import { getCommand } from "../utils/commands"
import { ClineProvider } from "../core/webview/ClineProvider"
import { ContextProxy } from "../core/config/ContextProxy"
import { focusPanel } from "../utils/focusPanel"

import { registerHumanRelayCallback, unregisterHumanRelayCallback, handleHumanRelayResponse } from "./humanRelay"
import { handleNewTask } from "./handleTask"
import { CodeIndexManager } from "../services/code-index/manager"
import { importSettingsWithFeedback } from "../core/config/importExport"
import { MdmService } from "../services/mdm/MdmService"
import { t } from "../i18n"

/**
 * Helper to get the visible ClineProvider instance or log if not found.
 */
export function getVisibleProviderOrLog(outputChannel: vscode.OutputChannel): ClineProvider | undefined {
	const visibleProvider = ClineProvider.getVisibleInstance()
	if (!visibleProvider) {
		outputChannel.appendLine("Cannot find any visible MultiAgent instances.")
		return undefined
	}
	return visibleProvider
}

// Store panel references in both modes
let sidebarPanel: vscode.WebviewView | undefined = undefined
let tabPanel: vscode.WebviewPanel | undefined = undefined

/**
 * Get the currently active panel
 * @returns WebviewPanelWebviewView
 */
export function getPanel(): vscode.WebviewPanel | vscode.WebviewView | undefined {
	return tabPanel || sidebarPanel
}

/**
 * Set panel references
 */
export function setPanel(
	newPanel: vscode.WebviewPanel | vscode.WebviewView | undefined,
	type: "sidebar" | "tab",
): void {
	if (type === "sidebar") {
		sidebarPanel = newPanel as vscode.WebviewView
		tabPanel = undefined
	} else {
		tabPanel = newPanel as vscode.WebviewPanel
		sidebarPanel = undefined
	}
}

export type RegisterCommandOptions = {
	context: vscode.ExtensionContext
	outputChannel: vscode.OutputChannel
	provider: ClineProvider
}

export const registerCommands = (options: RegisterCommandOptions) => {
	const { context } = options

	for (const [id, callback] of Object.entries(getCommandsMap(options))) {
		const command = getCommand(id as CommandId)
		context.subscriptions.push(vscode.commands.registerCommand(command, callback))
	}
}

