     1→/**
     2→ * Slice 9: FFprobe-based Audio Silence Detection
     3→ *
     4→ * Uses FFprobe's silencedetect filter to find actual audio silence
     5→ * (not just gaps between speech segments like Whisper).
     6→ *
     7→ * More accurate than Whisper gap detection because it analyzes
     8→ * actual audio levels, not transcribed speech boundaries.
     9→ */
    10→
    11→const { execFile } = require('child_process');
    12→const { promisify } = require('util');
    13→const execFileAsync = promisify(execFile);
    14→const { validateAudioPath, safeFFprobe } = require('./securityUtils');
    15→
    16→/**
    17→ * Detect silence regions in audio file using FFprobe silencedetect
    18→ *
    19→ * @param {string} wavPath - Path to WAV file
    20→ * @param {Object} options - Detection options
    21→ * @param {number} options.threshold - dB threshold (default: -30, range: -60 to -20)
    22→ * @param {number} options.minDuration - Minimum silence duration in seconds (default: 0.5)
    23→ * @param {number} options.padding - Buffer to add around silences in seconds (default: 0.1)
    24→ * @returns {Promise<Array<{start: number, end: number, duration: number}>>}
    25→ */
    26→async function detectAudioSilences(wavPath, options = {}) {
    27→  const {
    28→    threshold = -30,
    29→    minDuration = 0.5,
    30→    padding = 0.1
    31→  } = options;
    32→
    33→  // SECURITY: Validate input path to prevent path traversal
    34→  const pathValidation = await validateAudioPath(wavPath);
    35→  if (!pathValidation.valid) {
    36→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
    37→  }
    38→  const validatedPath = pathValidation.path;
    39→
    40→  // Validate threshold range
    41→  const safeThreshold = Math.max(-60, Math.min(-20, threshold));
    42→
    43→  // SECURITY: Use execFile with array arguments to prevent command injection
    44→  // Escape single quotes in path for lavfi filter
    45→  const escapedPath = validatedPath.replace(/'/g, "'\\''");
    46→  const filter = `amovie='${escapedPath}',silencedetect=n=${safeThreshold}dB:d=${minDuration}`;
    47→
    48→  const args = [
    49→    '-f', 'lavfi',
    50→    '-i', filter,
    51→    '-show_entries', 'frame_tags=lavfi.silence_start,lavfi.silence_end',
    52→    '-of', 'default=nw=1'
    53→  ];
    54→
    55→  console.log(`[SPLICE] FFprobe args: ${JSON.stringify(args)}`);
    56→
    57→  try {
    58→    const { stdout, stderr } = await safeFFprobe(args);
    59→
    60→    // silencedetect outputs to stderr
    61→    const output = stderr || stdout;
    62→    const silences = parseFFprobeOutput(output, padding);
    63→
    64→    console.log(`[SPLICE] FFprobe detected ${silences.length} silence(s) at threshold ${safeThreshold}dB`);
    65→    return silences;
    66→
    67→  } catch (error) {
    68→    // FFprobe may exit with error but still produce output
    69→    if (error.stderr) {
    70→      const silences = parseFFprobeOutput(error.stderr, padding);
    71→      if (silences.length > 0) {
    72→        console.log(`[SPLICE] FFprobe detected ${silences.length} silence(s) (with exit error)`);
    73→        return silences;
    74→      }
    75→    }
    76→    throw new Error(`FFprobe failed: ${error.message}`);
    77→  }
    78→}
    79→
    80→/**
    81→ * Parse FFprobe silencedetect output
    82→ *
    83→ * Output format:
    84→ * [silencedetect @ 0x...] silence_start: 1.234
    85→ * [silencedetect @ 0x...] silence_end: 2.567 | silence_duration: 1.333
    86→ *
    87→ * @param {string} output - FFprobe stderr output
    88→ * @param {number} padding - Padding to apply to silence boundaries
    89→ * @returns {Array<{start: number, end: number, duration: number}>}
    90→ */
    91→function parseFFprobeOutput(output, padding = 0) {
    92→  const silences = [];
    93→  const lines = output.split('\n');
    94→
    95→  let currentStart = null;
    96→
    97→  for (const line of lines) {
    98→    // Match silence_start
    99→    const startMatch = line.match(/silence_start:\s*([\d.]+)/);
   100→    if (startMatch) {
   101→      currentStart = parseFloat(startMatch[1]);
   102→      continue;
   103→    }
   104→
   105→    // Match silence_end
   106→    const endMatch = line.match(/silence_end:\s*([\d.]+)/);
   107→    if (endMatch && currentStart !== null) {
   108→      const end = parseFloat(endMatch[1]);
   109→
   110→      // Apply padding (shrink silence region)
   111→      const paddedStart = currentStart + padding;
   112→      const paddedEnd = end - padding;
   113→
   114→      // Only add if still valid after padding
   115→      if (paddedEnd > paddedStart) {
   116→        silences.push({
   117→          start: paddedStart,
   118→          end: paddedEnd,
   119→          duration: parseFloat((paddedEnd - paddedStart).toFixed(3))
   120→        });
   121→      }
   122→
   123→      currentStart = null;
   124→    }
   125→  }
   126→
   127→  return silences;
   128→}
   129→
   130→/**
   131→ * Get audio file duration using FFprobe
   132→ *
   133→ * @param {string} filePath - Path to audio file
   134→ * @returns {Promise<number>} Duration in seconds
   135→ */
   136→async function getAudioDuration(filePath) {
   137→  // SECURITY: Validate input path
   138→  const pathValidation = await validateAudioPath(filePath);
   139→  if (!pathValidation.valid) {
   140→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
   141→  }
   142→
   143→  // SECURITY: Use execFile with array arguments
   144→  const args = [
   145→    '-v', 'error',
   146→    '-show_entries', 'format=duration',
   147→    '-of', 'default=noprint_wrappers=1:nokey=1',
   148→    pathValidation.path
   149→  ];
   150→
   151→  try {
