     1→/**
     2→ * Slice 9: XML Processor for Premiere Pro
     3→ *
     4→ * Handles XMEML format (Final Cut Pro 7 XML) which is what
     5→ * Premiere Pro exports via File > Export > Final Cut Pro XML.
     6→ *
     7→ * Uses regex-based string manipulation to preserve original XML formatting
     8→ * while modifying clip boundaries.
     9→ *
    10→ * Key insight: Video and audio clips are LINKED - when splitting, we must
    11→ * coordinate the new IDs across all linked clips.
    12→ */
    13→
    14→const fs = require('fs').promises;
    15→
    16→/**
    17→ * XMEML Processor Class - String-based manipulation
    18→ */
    19→class XMEMLProcessor {
    20→  constructor() {
    21→    this.xmlContent = null;
    22→    this.timebase = 30;
    23→  }
    24→
    25→  async loadFromFile(filePath) {
    26→    this.xmlContent = await fs.readFile(filePath, 'utf8');
    27→    this.detectTimebase();
    28→    return this.xmlContent;
    29→  }
    30→
    31→  detectTimebase() {
    32→    const match = this.xmlContent.match(/<sequence[^>]*>[\s\S]*?<timebase>(\d+)<\/timebase>/);
    33→    if (match) {
    34→      this.timebase = parseInt(match[1]);
    35→      console.log(`[SPLICE] Detected timebase: ${this.timebase} fps`);
    36→    }
    37→  }
    38→
    39→  framesToSeconds(frames) {
    40→    return frames / this.timebase;
    41→  }
    42→
    43→  secondsToFrames(seconds) {
    44→    return Math.round(seconds * this.timebase);
    45→  }
    46→
    47→  /**
    48→   * Find all sequence clipitems with their positions and link info
    49→   */
    50→  findSequenceClipitems() {
    51→    const clips = [];
    52→    const clipRegex = /<clipitem\s+id="([^"]+)"[^>]*>([\s\S]*?)<\/clipitem>/g;
    53→    let match;
    54→
    55→    while ((match = clipRegex.exec(this.xmlContent)) !== null) {
    56→      const clipContent = match[2];
    57→
    58→      const startMatch = clipContent.match(/<start>(\d+)<\/start>/);
    59→      const endMatch = clipContent.match(/<end>(\d+)<\/end>/);
    60→
    61→      if (startMatch && endMatch) {
    62→        const inMatch = clipContent.match(/<in>(\d+)<\/in>/);
    63→        const outMatch = clipContent.match(/<out>(\d+)<\/out>/);
    64→        const nameMatch = clipContent.match(/<name>([^<]*)<\/name>/);
    65→        const durationMatch = clipContent.match(/<duration>(\d+)<\/duration>/);
    66→
    67→        // Extract all link references
    68→        const linkRefs = [];
    69→        const linkRegex = /<linkclipref>([^<]+)<\/linkclipref>/g;
    70→        let linkMatch;
    71→        while ((linkMatch = linkRegex.exec(clipContent)) !== null) {
    72→          linkRefs.push(linkMatch[1]);
    73→        }
    74→
    75→        clips.push({
    76→          id: match[1],
    77→          fullMatch: match[0],
    78→          startIndex: match.index,
    79→          endIndex: match.index + match[0].length,
    80→          content: clipContent,
    81→          start: parseInt(startMatch[1]),
    82→          end: parseInt(endMatch[1]),
    83→          in: inMatch ? parseInt(inMatch[1]) : 0,
    84→          out: outMatch ? parseInt(outMatch[1]) : parseInt(endMatch[1]),
    85→          duration: durationMatch ? parseInt(durationMatch[1]) : null,
    86→          name: nameMatch ? nameMatch[1] : 'Unknown',
    87→          linkRefs: linkRefs
    88→        });
    89→      }
    90→    }
    91→
    92→    return clips;
    93→  }
    94→
    95→  /**
    96→   * Group clips by their start time (linked clips have same start)
    97→   */
    98→  groupLinkedClips(clips) {
    99→    const groups = {};
   100→    for (const clip of clips) {
   101→      const key = clip.start;
   102→      if (!groups[key]) {
   103→        groups[key] = [];
   104→      }
   105→      groups[key].push(clip);
   106→    }
   107→    return Object.values(groups);
   108→  }
   109→
   110→  /**
   111→   * Create a modified clip with new timing and updated link references
   112→   */
   113→  createModifiedClip(originalClip, newStart, newEnd, newIn, newOut, idMapping) {
   114→    let modified = originalClip.fullMatch;
   115→
   116→    // Update timing values
   117→    modified = modified.replace(/<start>\d+<\/start>/, `<start>${newStart}</start>`);
   118→    modified = modified.replace(/<end>\d+<\/end>/, `<end>${newEnd}</end>`);
   119→    modified = modified.replace(/<in>\d+<\/in>/, `<in>${newIn}</in>`);
   120→    modified = modified.replace(/<out>\d+<\/out>/, `<out>${newOut}</out>`);
   121→
   122→    const newDuration = newOut - newIn;
   123→    modified = modified.replace(/<duration>\d+<\/duration>/, `<duration>${newDuration}</duration>`);
   124→
   125→    // Update pproTicks if present
   126→    const ticksPerFrame = 254016000000 / this.timebase;
   127→    modified = modified.replace(
   128→      /<pproTicksIn>\d+<\/pproTicksIn>/,
   129→      `<pproTicksIn>${Math.round(newIn * ticksPerFrame)}</pproTicksIn>`
   130→    );
   131→    modified = modified.replace(
   132→      /<pproTicksOut>\d+<\/pproTicksOut>/,
   133→      `<pproTicksOut>${Math.round(newOut * ticksPerFrame)}</pproTicksOut>`
   134→    );
   135→
   136→    // Update clip ID if in mapping
   137→    if (idMapping[originalClip.id]) {
   138→      modified = modified.replace(
   139→        new RegExp(`id="${originalClip.id}"`),
   140→        `id="${idMapping[originalClip.id]}"`
   141→      );
   142→    }
   143→
   144→    // Update ALL link references using the mapping
   145→    for (const [oldId, newId] of Object.entries(idMapping)) {
   146→      modified = modified.replace(
   147→        new RegExp(`<linkclipref>${oldId}</linkclipref>`, 'g'),
   148→        `<linkclipref>${newId}</linkclipref>`
   149→      );
   150→    }
   151→
   152→    return modified;
   153→  }
   154→
   155→  /**
   156→   * Split clips at silence boundaries - handles linked clips together
   157→   */
   158→  splitClipsAtSilences(silences, removeGaps = true) {
   159→    const stats = {
   160→      clipsProcessed: 0,
   161→      clipsSplit: 0,
   162→      silencesRemoved: 0,
   163→      totalTimeRemoved: 0,
   164→      cuts: []
   165→    };
   166→
   167→    if (!silences || silences.length === 0) {
   168→      return stats;
   169→    }
   170→
   171→    const sortedSilences = [...silences].sort((a, b) => a.start - b.start);
   172→    const clips = this.findSequenceClipitems();
   173→    console.log(`[SPLICE] Found ${clips.length} sequence clips`);
   174→
   175→    // Group linked clips (same start time = linked)
   176→    const linkedGroups = this.groupLinkedClips(clips);
   177→    console.log(`[SPLICE] Found ${linkedGroups.length} linked clip groups`);
   178→
   179→    // Sort groups by start position (descending for safe replacement)
   180→    linkedGroups.sort((a, b) => b[0].startIndex - a[0].startIndex);
   181→
   182→    let clipIdCounter = 1000;
   183→
   184→    for (const group of linkedGroups) {
   185→      // All clips in a group have the same timing
   186→      const refClip = group[0];
   187→      stats.clipsProcessed += group.length;
   188→
   189→      const clipStartSec = this.framesToSeconds(refClip.start);
   190→      const clipEndSec = this.framesToSeconds(refClip.end);
   191→
   192→      // Find silences that overlap with this group
   193→      const overlappingSilences = sortedSilences.filter(s =>
   194→        s.start < clipEndSec && s.end > clipStartSec
   195→      );
   196→
   197→      if (overlappingSilences.length === 0) {
   198→        continue;
   199→      }
   200→
   201→      // Calculate segments (non-silent portions)
   202→      const segments = this.calculateSegments(refClip, overlappingSilences);
   203→
   204→      if (segments.length === 0) {
   205→        // Entire clip group is silence - remove all
   206→        for (const clip of group.sort((a, b) => b.startIndex - a.startIndex)) {
   207→          this.xmlContent =
   208→            this.xmlContent.substring(0, clip.startIndex) +
   209→            this.xmlContent.substring(clip.endIndex);
   210→        }
   211→        continue;
   212→      }
   213→
   214→      console.log(`[SPLICE] Splitting ${group.length} linked clips into ${segments.length} segment(s)`);
   215→
   216→      // Track removed silences
   217→      for (const silence of overlappingSilences) {
   218→        const overlapStart = Math.max(silence.start, clipStartSec);
   219→        const overlapEnd = Math.min(silence.end, clipEndSec);
   220→        const duration = overlapEnd - overlapStart;
   221→
   222→        stats.silencesRemoved++;
   223→        stats.totalTimeRemoved += duration;
   224→        stats.cuts.push({
   225→          clipName: refClip.name,
   226→          clipType: 'video/audio',
   227→          silenceStart: overlapStart.toFixed(2),
   228→          silenceEnd: overlapEnd.toFixed(2),
   229→          duration: duration.toFixed(2)
   230→        });
   231→      }
   232→
   233→      // For each segment, create ID mappings for all clips in the group
   234→      const allReplacements = [];
   235→
   236→      for (let segIdx = 0; segIdx < segments.length; segIdx++) {
   237→        const seg = segments[segIdx];
   238→
   239→        // Create ID mapping for this segment
   240→        // First segment keeps original IDs, subsequent segments get new IDs
   241→        const idMapping = {};
   242→        if (segIdx > 0) {
   243→          for (const clip of group) {
   244→            idMapping[clip.id] = `clipitem-${clipIdCounter++}`;
   245→          }
   246→          stats.clipsSplit += group.length;
   247→        }
   248→
   249→        // Create modified clips for this segment
   250→        for (const clip of group) {
   251→          const modifiedClip = this.createModifiedClip(
   252→            clip,
   253→            seg.timelineStart,
   254→            seg.timelineEnd,
   255→            seg.sourceIn,
   256→            seg.sourceOut,
   257→            idMapping
   258→          );
   259→
   260→          allReplacements.push({
   261→            originalClip: clip,
   262→            modifiedClip: modifiedClip,
   263→            segmentIndex: segIdx
   264→          });
   265→        }
   266→      }
   267→
   268→      // Group replacements by original clip and sort by position (descending)
   269→      const clipReplacements = {};
   270→      for (const repl of allReplacements) {
   271→        const clipId = repl.originalClip.id;
   272→        if (!clipReplacements[clipId]) {
   273→          clipReplacements[clipId] = {
   274→            original: repl.originalClip,
   275→            segments: []
   276→          };
   277→        }
   278→        clipReplacements[clipId].segments.push(repl.modifiedClip);
   279→      }
   280→
   281→      // Apply replacements (process in reverse order of position)
   282→      const sortedClipIds = Object.keys(clipReplacements).sort((a, b) => {
   283→        return clipReplacements[b].original.startIndex - clipReplacements[a].original.startIndex;
   284→      });
   285→
   286→      for (const clipId of sortedClipIds) {
   287→        const { original, segments: segmentClips } = clipReplacements[clipId];
   288→        const replacement = segmentClips.join('\n');
   289→
   290→        this.xmlContent =
   291→          this.xmlContent.substring(0, original.startIndex) +
   292→          replacement +
   293→          this.xmlContent.substring(original.endIndex);
   294→      }
   295→    }
   296→
   297→    // Update sequence duration
   298→    this.updateSequenceDuration();
   299→
   300→    // Remove gaps if requested
   301→    if (removeGaps) {
   302→      this.removeGaps();
   303→    }
   304→
   305→    return stats;
   306→  }
   307→
   308→  /**
   309→   * Calculate non-silent segments from a clip
   310→   */
   311→  calculateSegments(clip, silences) {
   312→    const segments = [];
   313→    const clipStartSec = this.framesToSeconds(clip.start);
   314→    const clipEndSec = this.framesToSeconds(clip.end);
   315→    let currentTime = clipStartSec;
   316→
   317→    const mergedSilences = this.mergeSilences(silences);
   318→
   319→    for (const silence of mergedSilences) {
   320→      const silStartInClip = Math.max(silence.start, clipStartSec);
   321→      const silEndInClip = Math.min(silence.end, clipEndSec);
   322→
   323→      if (silStartInClip > currentTime) {
   324→        const segStartFrame = this.secondsToFrames(currentTime);
   325→        const segEndFrame = this.secondsToFrames(silStartInClip);
   326→        const offsetFrames = segStartFrame - clip.start;
   327→
   328→        segments.push({
   329→          timelineStart: segStartFrame,
   330→          timelineEnd: segEndFrame,
   331→          sourceIn: clip.in + offsetFrames,
   332→          sourceOut: clip.in + offsetFrames + (segEndFrame - segStartFrame)
   333→        });
   334→      }
   335→
   336→      currentTime = silEndInClip;
   337→    }
   338→
   339→    if (currentTime < clipEndSec) {
   340→      const segStartFrame = this.secondsToFrames(currentTime);
   341→      const segEndFrame = clip.end;
   342→      const offsetFrames = segStartFrame - clip.start;
   343→
   344→      segments.push({
   345→        timelineStart: segStartFrame,
   346→        timelineEnd: segEndFrame,
   347→        sourceIn: clip.in + offsetFrames,
   348→        sourceOut: clip.in + offsetFrames + (segEndFrame - segStartFrame)
   349→      });
   350→    }
   351→
   352→    return segments;
   353→  }
   354→
   355→  mergeSilences(silences) {
   356→    if (silences.length === 0) return [];
   357→
   358→    const sorted = [...silences].sort((a, b) => a.start - b.start);
   359→    const merged = [{ ...sorted[0] }];
   360→
   361→    for (let i = 1; i < sorted.length; i++) {
   362→      const current = sorted[i];
   363→      const last = merged[merged.length - 1];
   364→
   365→      if (current.start <= last.end) {
   366→        last.end = Math.max(last.end, current.end);
   367→      } else {
   368→        merged.push({ ...current });
   369→      }
   370→    }
   371→
   372→    return merged;
   373→  }
   374→
   375→  /**
   376→   * Update sequence duration based on clips
   377→   */
   378→  updateSequenceDuration() {
   379→    const clips = this.findSequenceClipitems();
   380→    if (clips.length === 0) return;
   381→
   382→    const maxEnd = Math.max(...clips.map(c => c.end));
   383→    console.log(`[SPLICE] Updating sequence duration to ${maxEnd} frames`);
   384→
   385→    // Update sequence duration
   386→    this.xmlContent = this.xmlContent.replace(
   387→      /(<sequence[^>]*>[\s\S]*?<duration>)\d+(<\/duration>)/,
   388→      `$1${maxEnd}$2`
   389→    );
   390→  }
   391→
   392→  /**
   393→   * Remove gaps by adjusting clip start/end times
   394→   */
   395→  removeGaps() {
   396→    const clips = this.findSequenceClipitems();
   397→    const linkedGroups = this.groupLinkedClips(clips);
   398→
   399→    // Sort by start time
   400→    linkedGroups.sort((a, b) => a[0].start - b[0].start);
   401→
   402→    let cumulativeShift = 0;
   403→    let lastEnd = 0;
   404→
   405→    for (const group of linkedGroups) {
   406→      const clipStart = group[0].start;
   407→      const clipEnd = group[0].end;
   408→      const gap = clipStart - lastEnd;
   409→
   410→      if (gap > 0) {
   411→        cumulativeShift += gap;
   412→        console.log(`[SPLICE] Found gap of ${gap} frames at position ${clipStart}`);
   413→      }
   414→
   415→      if (cumulativeShift > 0) {
   416→        const newStart = clipStart - cumulativeShift;
   417→        const newEnd = clipEnd - cumulativeShift;
   418→
   419→        for (const clip of group) {
   420→          // Update this clip's start/end
   421→          const updatedClip = clip.fullMatch
   422→            .replace(/<start>\d+<\/start>/, `<start>${newStart}</start>`)
   423→            .replace(/<end>\d+<\/end>/, `<end>${newEnd}</end>`);
   424→
   425→          this.xmlContent = this.xmlContent.replace(clip.fullMatch, updatedClip);
   426→        }
   427→      }
   428→
   429→      lastEnd = clipEnd - cumulativeShift;
   430→    }
   431→
   432→    // Update sequence duration after gap removal
   433→    if (cumulativeShift > 0) {
   434→      this.updateSequenceDuration();
   435→    }
   436→  }
   437→
   438→  analyzeSilences(silences) {
   439→    const stats = {
   440→      clipsProcessed: 0,
   441→      clipsSplit: 0,
   442→      silencesRemoved: 0,
   443→      totalTimeRemoved: 0,
   444→      cuts: []
   445→    };
   446→
   447→    const clips = this.findSequenceClipitems();
   448→
   449→    for (const clip of clips) {
   450→      stats.clipsProcessed++;
   451→
   452→      const clipStartSec = this.framesToSeconds(clip.start);
   453→      const clipEndSec = this.framesToSeconds(clip.end);
   454→
   455→      for (const silence of silences) {
   456→        if (silence.start < clipEndSec && silence.end > clipStartSec) {
   457→          const overlapStart = Math.max(silence.start, clipStartSec);
   458→          const overlapEnd = Math.min(silence.end, clipEndSec);
   459→          const duration = overlapEnd - overlapStart;
   460→
   461→          stats.silencesRemoved++;
   462→          stats.totalTimeRemoved += duration;
   463→          stats.cuts.push({
   464→            clipName: clip.name,
   465→            clipType: 'video/audio',
   466→            silenceStart: overlapStart.toFixed(2),
   467→            silenceEnd: overlapEnd.toFixed(2),
   468→            duration: duration.toFixed(2)
   469→          });
   470→
   471→          if (silence.start > clipStartSec && silence.end < clipEndSec) {
   472→            stats.clipsSplit++;
   473→          }
   474→        }
   475→      }
   476→    }
   477→
   478→    return stats;
   479→  }
   480→
   481→  async saveToFile(filePath) {
   482→    await fs.writeFile(filePath, this.xmlContent, 'utf8');
   483→    return filePath;
   484→  }
   485→}
   486→
   487→async function processXMLFile(inputPath, silences, options = {}) {
   488→  const { outputPath = null, removeGaps = true, analyzeOnly = false } = options;
   489→
   490→  const processor = new XMEMLProcessor();
   491→
   492→  console.log(`[SPLICE] Loading XMEML: ${inputPath}`);
   493→  await processor.loadFromFile(inputPath);
   494→
   495→  let stats;
   496→  if (analyzeOnly) {
   497→    console.log(`[SPLICE] Analyzing ${silences.length} silence region(s)`);
   498→    stats = processor.analyzeSilences(silences);
   499→  } else {
   500→    console.log(`[SPLICE] Splitting clips at ${silences.length} silence region(s)`);
   501→    stats = processor.splitClipsAtSilences(silences, removeGaps);
   502→  }
   503→
   504→  const finalOutputPath = outputPath ||
   505→    inputPath.replace(/\.xml$/i, '_processed.xml');
   506→
   507→  await processor.saveToFile(finalOutputPath);
   508→  console.log(`[SPLICE] Saved to: ${finalOutputPath}`);
   509→
   510→  return { outputPath: finalOutputPath, stats };
   511→}
   512→
   513→module.exports = { XMEMLProcessor, processXMLFile };
   514→

