```

**TanStack Query v5 Configuration:**

```typescript
// lib/query-client.ts
import { QueryClient } from '@tanstack/react-query';

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 1000 * 60 * 5, // 5 minutes
      gcTime: 1000 * 60 * 30,   // 30 minutes (renamed from cacheTime in v5)
      retry: 3,
      refetchOnWindowFocus: false,
    },
    mutations: {
      retry: 0,
    },
  },
});
```

Key v5 changes to note:
- `loading` status renamed to `pending`
- `isLoading` renamed to `isPending`
- `cacheTime` renamed to `gcTime`
- Stable Suspense support with `useSuspenseQuery`

### Audio Player Component with Waveform

**Recommendation: wavesurfer.js with @wavesurfer/react**

```typescript
// components/audio-player.tsx
import { useRef, useEffect, useState, useCallback } from 'react';
import WaveSurfer from 'wavesurfer.js';
import RegionsPlugin from 'wavesurfer.js/dist/plugins/regions';

interface AudioPlayerProps {
  audioUrl: string;
  onTimeUpdate?: (time: number) => void;
  onReady?: (duration: number) => void;
}

export function AudioPlayer({ audioUrl, onTimeUpdate, onReady }: AudioPlayerProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const wavesurferRef = useRef<WaveSurfer | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);

  useEffect(() => {
    if (!containerRef.current) return;

    const ws = WaveSurfer.create({
      container: containerRef.current,
      waveColor: 'hsl(var(--muted-foreground))',
      progressColor: 'hsl(var(--primary))',
      cursorColor: 'hsl(var(--primary))',
      barWidth: 2,
      barGap: 1,
      barRadius: 2,
      height: 80,
      normalize: true,
      plugins: [RegionsPlugin.create()],
    });

    ws.on('ready', () => {
      setDuration(ws.getDuration());
      onReady?.(ws.getDuration());
    });

    ws.on('audioprocess', () => {
      const time = ws.getCurrentTime();
      setCurrentTime(time);
      onTimeUpdate?.(time);
    });

    ws.on('play', () => setIsPlaying(true));
    ws.on('pause', () => setIsPlaying(false));

    ws.load(audioUrl);
    wavesurferRef.current = ws;

    return () => ws.destroy();
  }, [audioUrl]);

  const togglePlay = useCallback(() => {
    wavesurferRef.current?.playPause();
  }, []);

  return (
    <div className="space-y-4">
      <div 
        ref={containerRef} 
        className="w-full rounded-lg bg-muted p-2"
        role="application"
        aria-label="Audio waveform"
      />
      <div className="flex items-center gap-4">
        <button
          onClick={togglePlay}
          className="rounded-full bg-primary p-3 text-primary-foreground"
          aria-label={isPlaying ? 'Pause' : 'Play'}
        >
          {isPlaying ? <PauseIcon /> : <PlayIcon />}
        </button>
        <span className="text-sm text-muted-foreground">
          {formatTime(currentTime)} / {formatTime(duration)}
        </span>
      </div>
    </div>
  );
}
```

### Upload Interface with Drag-Drop and Progress

**Recommendation: react-dropzone + custom progress UI**

```typescript
// components/upload-dropzone.tsx
import { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Progress } from '@/components/ui/progress';
import { Upload, X, CheckCircle, AlertCircle } from 'lucide-react';

interface UploadFile {
  file: File;
  id: string;
  progress: number;
  status: 'pending' | 'uploading' | 'complete' | 'error';
  error?: string;
}

export function UploadDropzone() {
  const [files, setFiles] = useState<UploadFile[]>([]);
  const queryClient = useQueryClient();

  const uploadMutation = useMutation({
    mutationFn: async ({ file, onProgress }: { 
      file: File; 
      onProgress: (progress: number) => void 
    }) => {
      const formData = new FormData();
      formData.append('file', file);

      const xhr = new XMLHttpRequest();

      return new Promise((resolve, reject) => {
        xhr.upload.addEventListener('progress', (e) => {
          if (e.lengthComputable) {
            onProgress(Math.round((e.loaded / e.total) * 100));
          }
        });

        xhr.addEventListener('load', () => {
          if (xhr.status >= 200 && xhr.status < 300) {
            resolve(JSON.parse(xhr.response));
          } else {
            reject(new Error('Upload failed'));
          }
        });

        xhr.addEventListener('error', () => reject(new Error('Network error')));
        xhr.open('POST', '/api/upload');
        xhr.send(formData);
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['jobs'] });
    },
  });

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const newFiles = acceptedFiles.map((file) => ({
      file,
      id: crypto.randomUUID(),
      progress: 0,
      status: 'pending' as const,
    }));

    setFiles((prev) => [...prev, ...newFiles]);

    newFiles.forEach((uploadFile) => {
      setFiles((prev) =>
        prev.map((f) =>
          f.id === uploadFile.id ? { ...f, status: 'uploading' } : f
        )
      );

      uploadMutation.mutate(
        {
          file: uploadFile.file,
          onProgress: (progress) => {
            setFiles((prev) =>
              prev.map((f) =>
                f.id === uploadFile.id ? { ...f, progress } : f
              )
            );
          },
        },
        {
          onSuccess: () => {
            setFiles((prev) =>
              prev.map((f) =>
                f.id === uploadFile.id
                  ? { ...f, status: 'complete', progress: 100 }
                  : f
              )
            );
          },
          onError: (error) => {
            setFiles((prev) =>
              prev.map((f) =>
                f.id === uploadFile.id
                  ? { ...f, status: 'error', error: error.message }
                  : f
              )
            );
          },
        }
      );
    });
  }, [uploadMutation]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'audio/*': ['.mp3', '.wav', '.flac', '.aac', '.ogg'],
    },
    maxSize: 500 * 1024 * 1024, // 500MB
  });

  return (
    <div className="space-y-4">
      <div
        {...getRootProps()}
        className={`
          border-2 border-dashed rounded-lg p-8 text-center cursor-pointer
          transition-colors duration-200
          ${isDragActive 
            ? 'border-primary bg-primary/5' 
            : 'border-muted-foreground/25 hover:border-primary/50'
          }
        `}
      >
        <input {...getInputProps()} />
        <Upload className="mx-auto h-12 w-12 text-muted-foreground" />
        <p className="mt-2 text-sm text-muted-foreground">
          {isDragActive
            ? 'Drop audio files here...'
            : 'Drag & drop audio files, or click to select'}
        </p>
        <p className="mt-1 text-xs text-muted-foreground">
          Supports MP3, WAV, FLAC, AAC, OGG (max 500MB)
        </p>
      </div>

      {files.length > 0 && (
        <div className="space-y-2">
          {files.map((file) => (
            <UploadFileCard key={file.id} file={file} />
          ))}
        </div>
      )}
    </div>
  );
}

function UploadFileCard({ file }: { file: UploadFile }) {
  return (
    <div className="flex items-center gap-3 rounded-lg border p-3">
      <div className="flex-1 min-w-0">
        <p className="text-sm font-medium truncate">{file.file.name}</p>
        <p className="text-xs text-muted-foreground">
          {(file.file.size / 1024 / 1024).toFixed(2)} MB
        </p>
      </div>

      {file.status === 'uploading' && (
        <div className="w-32">
          <Progress value={file.progress} className="h-2" />
        </div>
      )}

      {file.status === 'complete' && (
        <CheckCircle className="h-5 w-5 text-green-500" />
      )}

      {file.status === 'error' && (
        <AlertCircle className="h-5 w-5 text-destructive" />
      )}
    </div>
  );
}
```

### Job Queue Visualization

```typescript
// components/job-queue.tsx
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Clock, Loader2, CheckCircle2, XCircle } from 'lucide-react';

type JobStatus = 'pending' | 'processing' | 'complete' | 'failed';

interface Job {
  id: string;
  name: string;
  status: JobStatus;
  progress?: number;
  createdAt: string;
  completedAt?: string;
  error?: string;
}

export function JobQueue() {
  const { data: jobs, isPending, error } = useQuery<Job[]>({
    queryKey: ['jobs'],
    queryFn: () => fetch('/api/jobs').then((res) => res.json()),
    refetchInterval: 3000, // Poll every 3 seconds
  });

  if (isPending) {
    return <JobQueueSkeleton />;
  }

  if (error) {
    return (
      <Card className="border-destructive">
        <CardContent className="pt-6">
          <p className="text-destructive">Failed to load jobs</p>
        </CardContent>
      </Card>
    );
  }

  const groupedJobs = {
    processing: jobs?.filter((j) => j.status === 'processing') ?? [],
    pending: jobs?.filter((j) => j.status === 'pending') ?? [],
    complete: jobs?.filter((j) => j.status === 'complete') ?? [],
    failed: jobs?.filter((j) => j.status === 'failed') ?? [],
  };

  return (
    <div className="space-y-6">
      {groupedJobs.processing.length > 0 && (
        <JobSection title="Processing" jobs={groupedJobs.processing} />
      )}
      {groupedJobs.pending.length > 0 && (
        <JobSection title="Pending" jobs={groupedJobs.pending} />
      )}
      {groupedJobs.complete.length > 0 && (
        <JobSection title="Complete" jobs={groupedJobs.complete} />
      )}
      {groupedJobs.failed.length > 0 && (
        <JobSection title="Failed" jobs={groupedJobs.failed} />
      )}
    </div>
  );
}

function JobSection({ title, jobs }: { title: string; jobs: Job[] }) {
  return (
    <div>
      <h3 className="text-sm font-medium text-muted-foreground mb-3">
        {title} ({jobs.length})
      </h3>
      <div className="space-y-2">
        {jobs.map((job) => (
          <JobCard key={job.id} job={job} />
        ))}
      </div>
    </div>
  );
}

function JobCard({ job }: { job: Job }) {
  const statusConfig = {
    pending: { icon: Clock, color: 'bg-yellow-500/10 text-yellow-600' },
    processing: { icon: Loader2, color: 'bg-blue-500/10 text-blue-600' },
    complete: { icon: CheckCircle2, color: 'bg-green-500/10 text-green-600' },
    failed: { icon: XCircle, color: 'bg-red-500/10 text-red-600' },
  };

  const { icon: Icon, color } = statusConfig[job.status];

  return (
    <Card>
      <CardContent className="flex items-center gap-4 p-4">
        <div className={`rounded-full p-2 ${color}`}>
          <Icon 
            className={`h-4 w-4 ${job.status === 'processing' ? 'animate-spin' : ''}`} 
          />
        </div>

        <div className="flex-1 min-w-0">
          <p className="font-medium truncate">{job.name}</p>
          <p className="text-xs text-muted-foreground">
            {new Date(job.createdAt).toLocaleString()}
          </p>
        </div>

        {job.status === 'processing' && job.progress !== undefined && (
          <Badge variant="secondary">{job.progress}%</Badge>
        )}

        <Badge variant={job.status === 'failed' ? 'destructive' : 'outline'}>
          {job.status}
        </Badge>
      </CardContent>
    </Card>
  );
}

function JobQueueSkeleton() {
  return (
    <div className="space-y-2">
      {[...Array(3)].map((_, i) => (
        <Card key={i}>
          <CardContent className="flex items-center gap-4 p-4">
            <Skeleton className="h-8 w-8 rounded-full" />
            <div className="flex-1 space-y-2">
              <Skeleton className="h-4 w-48" />
              <Skeleton className="h-3 w-24" />
            </div>
            <Skeleton className="h-6 w-16" />
          </CardContent>
        </Card>
      ))}
    </div>
  );
}
```

