### Host App Theme Synchronization

```typescript
// lib/theme.ts
import type { CSInterface } from 'types/csinterface';

interface HostTheme {
  panelBackgroundColor: string;
  baseFontSize: number;
  appSkinInfo: {
    panelBackgroundColor: RGBColor;
    systemHighlightColor: RGBColor;
  };
}

interface RGBColor {
  color: { red: number; green: number; blue: number; alpha: number };
}

export function getHostTheme(csInterface: CSInterface): 'light' | 'dark' {
  const hostEnv = csInterface.getHostEnvironment();
  const skinInfo = JSON.parse(hostEnv).appSkinInfo;

  // Calculate luminance from panel background
  const { red, green, blue } = skinInfo.panelBackgroundColor.color;
  const luminance = (0.299 * red + 0.587 * green + 0.114 * blue) / 255;

  return luminance > 0.5 ? 'light' : 'dark';
}

export function applyHostTheme(csInterface: CSInterface) {
  const hostEnv = csInterface.getHostEnvironment();
  const skinInfo = JSON.parse(hostEnv).appSkinInfo;

  const bgColor = skinInfo.panelBackgroundColor.color;
  const root = document.documentElement;

  root.style.setProperty('--panel-bg', 
    `rgb(${bgColor.red}, ${bgColor.green}, ${bgColor.blue})`
  );
  root.style.setProperty('--panel-text', 
    getHostTheme(csInterface) === 'dark' ? '#e0e0e0' : '#323232'
  );
}
