### Responsive Panel Sizing

```typescript
// components/Panel.tsx
import { useEffect, useState } from 'react';

type PanelMode = 'docked' | 'floating';

export function Panel({ children }: { children: React.ReactNode }) {
  const [mode, setMode] = useState<PanelMode>('docked');
  const [dimensions, setDimensions] = useState({ width: 300, height: 400 });

  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;

      setDimensions({ width, height });

      // Detect if panel is floating (typically larger dimensions)
      setMode(width > 400 || height > 600 ? 'floating' : 'docked');
    };

    window.addEventListener('resize', handleResize);
    handleResize();

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div 
      className={`panel ${mode}`}
      style={{
        '--panel-width': `${dimensions.width}px`,
        '--panel-height': `${dimensions.height}px`,
      } as React.CSSProperties}
    >
      {children}
    </div>
  );
}
