### Audio Preview Player (Shared)

```typescript
// packages/shared-ui/src/components/AudioPreview/AudioPreview.tsx
import { forwardRef, useImperativeHandle, useRef, useState } from 'react';

export interface AudioPreviewProps {
  src: string;
  title?: string;
  duration?: number;
  showWaveform?: boolean;
  compact?: boolean;
  onPlay?: () => void;
  onPause?: () => void;
  onSeek?: (time: number) => void;
  onEnded?: () => void;
}

export interface AudioPreviewRef {
  play: () => void;
  pause: () => void;
  seek: (time: number) => void;
  getCurrentTime: () => number;
}

export const AudioPreview = forwardRef<AudioPreviewRef, AudioPreviewProps>(
  function AudioPreview(
    { src, title, duration, showWaveform = true, compact = false, ...handlers },
    ref
  ) {
    const audioRef = useRef<HTMLAudioElement>(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [loadedDuration, setLoadedDuration] = useState(duration ?? 0);

    useImperativeHandle(ref, () => ({
      play: () => audioRef.current?.play(),
      pause: () => audioRef.current?.pause(),
      seek: (time) => {
        if (audioRef.current) audioRef.current.currentTime = time;
      },
      getCurrentTime: () => audioRef.current?.currentTime ?? 0,
    }));

    const handlePlayPause = () => {
      if (isPlaying) {
        audioRef.current?.pause();
      } else {
        audioRef.current?.play();
      }
    };

    const handleTimeUpdate = () => {
      setCurrentTime(audioRef.current?.currentTime ?? 0);
    };

    return (
      <div 
        className={`audio-preview ${compact ? 'compact' : ''}`}
        role="region"
        aria-label={`Audio player: ${title ?? 'Untitled'}`}
      >
        <audio
          ref={audioRef}
          src={src}
          onPlay={() => {
            setIsPlaying(true);
            handlers.onPlay?.();
          }}
          onPause={() => {
            setIsPlaying(false);
            handlers.onPause?.();
          }}
          onTimeUpdate={handleTimeUpdate}
          onLoadedMetadata={() => setLoadedDuration(audioRef.current?.duration ?? 0)}
          onEnded={handlers.onEnded}
          onSeeked={() => handlers.onSeek?.(audioRef.current?.currentTime ?? 0)}
        />

        {showWaveform && !compact && (
          <WaveformDisplay src={src} currentTime={currentTime} duration={loadedDuration} />
        )}

        <div className="audio-preview-controls">
          <button
            onClick={handlePlayPause}
            aria-label={isPlaying ? 'Pause' : 'Play'}
            className="play-button"
          >
            {isPlaying ? <PauseIcon /> : <PlayIcon />}
          </button>

          <ProgressSlider
            value={currentTime}
            max={loadedDuration}
            onChange={(value) => {
              if (audioRef.current) audioRef.current.currentTime = value;
            }}
          />

          <TimeDisplay current={currentTime} total={loadedDuration} />
        </div>

        {title && <p className="audio-preview-title">{title}</p>}
      </div>
    );
  }
);
