## 4. Accessibility & UX

### Keyboard Navigation Implementation

```typescript
// hooks/useKeyboardNavigation.ts
import { useCallback, useEffect, useRef } from 'react';

interface UseKeyboardNavigationOptions {
  items: string[];
  onSelect: (index: number) => void;
  orientation?: 'horizontal' | 'vertical';
  loop?: boolean;
}

export function useKeyboardNavigation({
  items,
  onSelect,
  orientation = 'vertical',
  loop = true,
}: UseKeyboardNavigationOptions) {
  const focusedIndex = useRef(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getNextIndex = useCallback((current: number, direction: 1 | -1) => {
    const next = current + direction;
    if (loop) {
      return (next + items.length) % items.length;
    }
    return Math.max(0, Math.min(items.length - 1, next));
  }, [items.length, loop]);

  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    const prevKey = orientation === 'vertical' ? 'ArrowUp' : 'ArrowLeft';
    const nextKey = orientation === 'vertical' ? 'ArrowDown' : 'ArrowRight';

    switch (event.key) {
      case prevKey:
        event.preventDefault();
        focusedIndex.current = getNextIndex(focusedIndex.current, -1);
        focusItem(focusedIndex.current);
        break;
      case nextKey:
        event.preventDefault();
        focusedIndex.current = getNextIndex(focusedIndex.current, 1);
        focusItem(focusedIndex.current);
        break;
      case 'Enter':
      case ' ':
        event.preventDefault();
        onSelect(focusedIndex.current);
        break;
      case 'Home':
        event.preventDefault();
        focusedIndex.current = 0;
        focusItem(0);
        break;
      case 'End':
        event.preventDefault();
        focusedIndex.current = items.length - 1;
        focusItem(items.length - 1);
        break;
    }
  }, [orientation, getNextIndex, onSelect, items.length]);

  const focusItem = (index: number) => {
    const container = containerRef.current;
    if (!container) return;

    const items = container.querySelectorAll('[role="option"], [data-focusable]');
    const item = items[index] as HTMLElement;
    item?.focus();
  };

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    container.addEventListener('keydown', handleKeyDown);
    return () => container.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  return { containerRef, focusedIndex: focusedIndex.current };
}
