    29// Import services
    30const usageTracking = require('./services/usageTracking');
    31const { requireCredits } = require('./middleware/rateLimiter');
    32const { generateToken, generateRefreshToken, verifyToken, authenticateToken, maskSensitiveData } = require('./middleware/auth');
    33const referralService = require('./services/referralService');
    34const licenseService = require('./services/licenseService');
    35const { getSupportedLanguages, getAvailableBleepSounds } = require('./services/profanityDetection');
    36const { getSupportedFormats } = require('./services/captionExporter');
    37
    38// Import route modules
    39const healthRoutes = require('./routes/health');
    40const analyzeRoutes = require('./routes/analyze');
    41const silencesRoutes = require('./routes/silences');
    42const detectionRoutes = require('./routes/detection');
