    52const batchRoutes = require('./routes/batch');
    53const authRoutes = require('./routes/auth');
    54const billingRoutes = require('./routes/billing');
    55const referralRoutes = require('./routes/referral');
    56const licenseRoutes = require('./routes/license');
    57const musicRoutes = require('./routes/music');
    58
    59// Stripe for webhooks
    60const Stripe = require('stripe');
    61
    62// Validate STRIPE_SECRET_KEY in production - exit if missing
    63if (isProduction && !process.env.STRIPE_SECRET_KEY) {
    64  console.error('[SPLICE] CRITICAL: STRIPE_SECRET_KEY is required in production');
    65  process.exit(1);
    66}
    67
    68const stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
