      return res.status(413).json({ error: sizeCheck.error });
    }

    // Check FFprobe availability (needed for audio extraction)
    const ffprobeAvailable = await isFFprobeInstalled();
    if (!ffprobeAvailable) {
      return res.status(500).json({
