      // Deduct usage based on audio duration
      const audioDuration = result.metadata?.audioDuration || 0;
      let balance = null;
      if (audioDuration > 0 && req.deductUsage) {
        balance = await req.deductUsage(audioDuration);
      }

      res.json({
