   863        const jobId = `timeline_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
   864        r2Key = `${customerId}/${jobId}/timeline.wav`;
   865
   866        // Note: uploadMusic signature is (buffer, key, metadata)
