   119        processed_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
   120      )
   121    `);
   122
   123    await client.query(`
   124      CREATE INDEX IF NOT EXISTS idx_webhook_events_event_id ON webhook_events(event_id)
   125    `);
   126
   127    console.log('[UsageTracking] Database initialized');
   128  } finally {
   129    client.release();
   130  }
   131}
   132
   133/**
   134 * Get or create a user by Stripe customer ID
   135 * PERF-005: Uses INSERT...ON CONFLICT upsert for single-query operation
   136 */
   137async function getOrCreateUser(stripeCustomerId, email = null) {
   138  // Use upsert with ON CONFLICT DO UPDATE to ensure RETURNING works for both cases
   139  // The update sets stripe_customer_id = EXCLUDED.stripe_customer_id (no-op) to trigger RETURNING
   140  const result = await pool.query(
   141    `INSERT INTO users (stripe_customer_id, email, tier, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total)
   142     VALUES ($1, $2, 'starter', $3, $3, $4, $4)
   143     ON CONFLICT (stripe_customer_id) DO UPDATE
   144     SET stripe_customer_id = EXCLUDED.stripe_customer_id
   145     RETURNING *`,
   146    [stripeCustomerId, email, TIER_HOURS.starter, TIER_ISOLATION_HOURS.starter]
   147  );
   148
   149  return result.rows[0];
   150}
   151
   152/**
   153 * Get user's current credit balance
   154 */
   155async function getBalance(stripeCustomerId) {
   156  const user = await getOrCreateUser(stripeCustomerId);
   157  const hoursTotal = parseFloat(user.hours_total);
   158  const hoursRemaining = parseFloat(user.hours_remaining);
   159  const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   160  const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   161
   162  // Tier display names
   163  const tierNames = {
   164    starter: 'Starter',
   165    pro: 'Pro',
   166    team: 'Team',
   167    cancelled: 'Cancelled'
   168  };
   169
   170  return {
   171    hoursRemaining,
   172    hoursTotal,
   173    isolationHoursRemaining,
   174    isolationHoursTotal,
   175    tier: user.tier,
   176    tierName: tierNames[user.tier] || user.tier,
   177    percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   178    isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   179    hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   180    isolationOverageRate: ISOLATION_OVERAGE_RATE
   181  };
   182}
   183
   184/**
   185 * Check if user has enough credits for estimated duration
   186 */
   187async function hasCredits(stripeCustomerId, estimatedSeconds = 0) {
   188  const balance = await getBalance(stripeCustomerId);
   189  const estimatedHours = estimatedSeconds / 3600;
   190  return balance.hoursRemaining >= estimatedHours;
   191}
   192
   193/**
   194 * Reserve credits atomically using SELECT ... FOR UPDATE
   195 * This prevents race conditions where multiple requests pass credit check
   196 * before any deduction occurs.
   197 *
   198 * @param {string} stripeCustomerId - Customer ID
   199 * @param {number} estimatedSeconds - Estimated processing time in seconds
   200 * @param {string} endpoint - Endpoint name for tracking
   201 * @returns {Promise<{success: boolean, reservationId?: string, error?: string}>}
   202 */
   203async function reserveCredits(stripeCustomerId, estimatedSeconds = 0, endpoint = 'unknown') {
   204  const estimatedHours = estimatedSeconds / 3600;
   205  const client = await pool.connect();
   206
   207  try {
   208    await client.query('BEGIN');
   209
   210    // Lock the row for this customer - prevents concurrent modifications
   211    const result = await client.query(
   212      `SELECT id, hours_remaining FROM users
   213       WHERE stripe_customer_id = $1
   214       FOR UPDATE`,
   215      [stripeCustomerId]
   216    );
   217
   218    if (result.rows.length === 0) {
   219      // Create user if doesn't exist (within transaction)
   220      const newUser = await client.query(
   221        `INSERT INTO users (stripe_customer_id, tier, hours_remaining, hours_total)
   222         VALUES ($1, 'starter', $2, $2)
   223         RETURNING id, hours_remaining`,
   224        [stripeCustomerId, TIER_HOURS.starter]
   225      );
   226      result.rows = newUser.rows;
   227    }
   228
   229    const user = result.rows[0];
   230    const hoursRemaining = parseFloat(user.hours_remaining);
   231
   232    // Check if enough credits
   233    if (hoursRemaining < estimatedHours) {
   234      await client.query('ROLLBACK');
   235      return {
   236        success: false,
   237        error: 'Insufficient credits',
   238        hoursRemaining,
   239        hoursRequired: estimatedHours
   240      };
   241    }
   242
   243    // Generate unique reservation ID
   244    const reservationId = `res_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   245
   246    // Reserve credits by deducting the estimated amount
   247    await client.query(
   248      `UPDATE users
   249       SET hours_remaining = hours_remaining - $1,
   250           updated_at = CURRENT_TIMESTAMP
   251       WHERE stripe_customer_id = $2`,
   252      [estimatedHours, stripeCustomerId]
   253    );
   254
   255    // Log the reservation
   256    await client.query(
   257      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   258       VALUES ($1, $2, $3, $4, $5)`,
   259      [user.id, stripeCustomerId, estimatedSeconds, estimatedHours, `${endpoint}:reserved`]
   260    );
   261
   262    await client.query('COMMIT');
   263
   264    console.log(`[UsageTracking] Reserved ${estimatedHours.toFixed(4)}hrs for ${stripeCustomerId} (${reservationId})`);
   265
   266    return {
   267      success: true,
   268      reservationId,
   269      hoursReserved: estimatedHours,
   270      hoursRemaining: hoursRemaining - estimatedHours
   271    };
   272  } catch (err) {
   273    await client.query('ROLLBACK');
   274    console.error('[UsageTracking] Reservation error:', err);
   275    throw err;
   276  } finally {
   277    client.release();
   278  }
   279}
   280
   281/**
   282 * Confirm a reservation (finalize usage) or adjust for actual duration
   283 * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   284 *
   285 * @param {string} stripeCustomerId - Customer ID
   286 * @param {number} actualSeconds - Actual processing time in seconds
   287 * @param {number} reservedSeconds - Originally reserved seconds
   288 * @param {string} endpoint - Endpoint name
   289 * @returns {Promise<object>} Updated balance
   290 */
   291async function confirmReservation(stripeCustomerId, actualSeconds, reservedSeconds, endpoint = 'unknown') {
   292  const actualHours = actualSeconds / 3600;
   293  const reservedHours = reservedSeconds / 3600;
   294  const difference = reservedHours - actualHours;
   295
   296  const client = await pool.connect();
   297  try {
   298    await client.query('BEGIN');
   299
   300    // Lock the row and get current state
   301    const result = await client.query(
   302      `SELECT id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier
   303       FROM users
   304       WHERE stripe_customer_id = $1
   305       FOR UPDATE`,
   306      [stripeCustomerId]
   307    );
   308
   309    if (result.rows.length === 0) {
   310      await client.query('ROLLBACK');
   311      throw new Error('User not found');
   312    }
   313
   314    let user = result.rows[0];
   315
   316    // If actual usage was less than reserved, refund the difference
   317    if (difference > 0) {
   318      const updateResult = await client.query(
   319        `UPDATE users
   320         SET hours_remaining = hours_remaining + $1,
   321             updated_at = CURRENT_TIMESTAMP
   322         WHERE stripe_customer_id = $2
