   479/**
   480 * Check if user can use vocal isolation
   481 * Returns: { allowed, hasIncludedHours, overageRequired, overageCost }
   482 */
   483async function checkIsolationAccess(stripeCustomerId, estimatedMinutes = 0) {
   484  const balance = await getBalance(stripeCustomerId);
   485
   486  // Starter tier has no isolation access
   487  if (!balance.hasIsolationAccess) {
   488    return {
   489      allowed: false,
   490      reason: 'upgrade_required',
   491      message: 'Vocal isolation requires Pro or Team tier',
   492      hasIncludedHours: false,
   493      overageRequired: false,
   494      overageCost: 0
   495    };
   496  }
   497
   498  const estimatedHours = estimatedMinutes / 60;
   499  const remainingIncluded = balance.isolationHoursRemaining;
   500
   501  // Check if within included hours
   502  if (remainingIncluded >= estimatedHours) {
   503    return {
   504      allowed: true,
   505      reason: 'included',
   506      message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   507      hasIncludedHours: true,
   508      overageRequired: false,
   509      overageCost: 0,
   510      isolationHoursRemaining: remainingIncluded - estimatedHours
   511    };
   512  }
   513
   514  // Calculate overage
   515  const overageMinutes = estimatedMinutes - (remainingIncluded * 60);
   516  const overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   517
   518  return {
   519    allowed: true,
   520    reason: 'overage',
   521    message: `${remainingIncluded > 0 ? `Using ${(remainingIncluded * 60).toFixed(1)} included min + ` : ''}${overageMinutes.toFixed(1)} min overage ($${overageCost.toFixed(2)})`,
   522    hasIncludedHours: remainingIncluded > 0,
   523    overageRequired: true,
   524    overageMinutes,
   525    overageCost,
   526    isolationHoursRemaining: 0
   527  };
   528}
