   530/**
   531 * Deduct isolation usage from user's balance
   532 * Returns overage cost if beyond included hours
   533 * Uses SELECT...FOR UPDATE to prevent race conditions (like confirmReservation)
   534 */
   535async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   536  const durationMinutes = audioDurationSeconds / 60;
   537  const durationHours = audioDurationSeconds / 3600;
   538
   539  const client = await pool.connect();
   540  try {
   541    await client.query('BEGIN');
   542
   543    // Lock the row for this customer - prevents concurrent modifications
   544    const userResult = await client.query(
   545      `SELECT id, isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier
   546       FROM users WHERE stripe_customer_id = $1
   547       FOR UPDATE`,
   548      [stripeCustomerId]
   549    );
   550
   551    if (userResult.rows.length === 0) {
   552      await client.query('ROLLBACK');
   553      throw new Error('User not found');
   554    }
   555
   556    const user = userResult.rows[0];
   557    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   558
   559    let overageCost = 0;
   560    let hoursFromIncluded = 0;
   561    let overageMinutes = 0;
   562
   563    if (remainingIncluded >= durationHours) {
   564      // Fully covered by included hours
   565      hoursFromIncluded = durationHours;
   566    } else {
   567      // Partial or full overage
   568      hoursFromIncluded = remainingIncluded;
   569      overageMinutes = durationMinutes - (remainingIncluded * 60);
   570      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   571    }
   572
   573    // Update isolation balance with RETURNING to get new values
   574    const updateResult = await client.query(
   575      `UPDATE users
   576       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   577           updated_at = CURRENT_TIMESTAMP
   578       WHERE stripe_customer_id = $2
   579       RETURNING isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier`,
   580      [durationHours, stripeCustomerId]
   581    );
   582
   583    const updatedUser = updateResult.rows[0];
   584
   585    // Log usage (mark as isolation)
   586    await client.query(
   587      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   588       VALUES ($1, $2, $3, $4, $5)`,
   589      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   590    );
   591
   592    await client.query('COMMIT');
   593
   594    // Build balance from RETURNING data (no extra SELECT needed)
   595    const hoursTotal = parseFloat(updatedUser.hours_total);
   596    const hoursRemaining = parseFloat(updatedUser.hours_remaining);
   597    const isolationHoursTotal = parseFloat(updatedUser.isolation_hours_total || 0);
   598    const isolationHoursRemaining = parseFloat(updatedUser.isolation_hours_remaining || 0);
   599
   600    const tierNames = {
   601      starter: 'Starter',
   602      pro: 'Pro',
   603      team: 'Team',
   604      cancelled: 'Cancelled'
   605    };
   606
   607    return {
   608      hoursRemaining,
   609      hoursTotal,
   610      isolationHoursRemaining,
   611      isolationHoursTotal,
   612      tier: updatedUser.tier,
   613      tierName: tierNames[updatedUser.tier] || updatedUser.tier,
   614      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   615      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   616      hasIsolationAccess: TIER_ISOLATION_HOURS[updatedUser.tier] > 0,
   617      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   618      isolationUsed: {
   619        durationMinutes,
   620        hoursFromIncluded,
   621        overageMinutes,
   622        overageCost
   623      }
   624    };
   625  } catch (err) {
   626    await client.query('ROLLBACK');
   627    throw err;
   628  } finally {
   629    client.release();
   630  }
   631}
