   633/**
   634 * Deduct usage from user's balance
   635 * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   636 * Uses transaction with rollback on failure for atomic UPDATE + INSERT
   637 * Reports overage to Stripe for billing when user exceeds included hours
   638 */
   639async function deductUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'unknown') {
   640  const hoursUsed = audioDurationSeconds / 3600;
   641
   642  const client = await pool.connect();
   643  try {
   644    await client.query('BEGIN');
   645
   646    // First get current balance to calculate overage
   647    const currentResult = await client.query(
   648      `SELECT id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier
   649       FROM users WHERE stripe_customer_id = $1 FOR UPDATE`,
   650      [stripeCustomerId]
   651    );
   652
   653    if (currentResult.rows.length === 0) {
   654      await client.query('ROLLBACK');
   655      throw new Error('User not found');
   656    }
   657
   658    const currentUser = currentResult.rows[0];
   659    const currentRemaining = parseFloat(currentUser.hours_remaining);
   660
   661    // Calculate overage (hours used beyond what's remaining)
   662    let overageHours = 0;
   663    if (hoursUsed > currentRemaining) {
   664      overageHours = hoursUsed - currentRemaining;
   665    }
   666
   667    // Update balance (clamp at 0)
   668    const updateResult = await client.query(
   669      `UPDATE users
   670       SET hours_remaining = GREATEST(0, hours_remaining - $1),
   671           updated_at = CURRENT_TIMESTAMP
   672       WHERE stripe_customer_id = $2
   673       RETURNING id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   674      [hoursUsed, stripeCustomerId]
   675    );
   676
   677    const user = updateResult.rows[0];
   678
   679    // Log usage
   680    await client.query(
   681      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   682       VALUES ($1, $2, $3, $4, $5)`,
   683      [user.id, stripeCustomerId, audioDurationSeconds, hoursUsed, endpoint]
   684    );
   685
   686    await client.query('COMMIT');
   687
   688    // Report overage to Stripe if any (after commit to not block transaction)
   689    let overageCharge = 0;
   690    if (overageHours > 0) {
   691      overageCharge = overageHours * HOURS_OVERAGE_RATE;
   692      const usageReport = await reportHoursOverage(stripeCustomerId, overageHours);
   693      if (usageReport.success) {
   694        console.log(`[SPLICE] Hours overage reported to Stripe: ${stripeCustomerId} - ${overageHours.toFixed(2)}hrs ($${overageCharge.toFixed(2)})`);
   695      } else {
   696        console.warn(`[SPLICE] Hours overage billing failed: ${usageReport.error}`);
   697      }
   698    }
   699
   700    // Build balance from RETURNING data (no extra SELECT needed)
   701    const hoursTotal = parseFloat(user.hours_total);
   702    const hoursRemaining = parseFloat(user.hours_remaining);
   703    const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   704    const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   705
   706    const tierNames = {
   707      starter: 'Starter',
   708      pro: 'Pro',
   709      team: 'Team',
   710      cancelled: 'Cancelled'
   711    };
   712
   713    return {
   714      hoursRemaining,
   715      hoursTotal,
   716      isolationHoursRemaining,
   717      isolationHoursTotal,
   718      tier: user.tier,
   719      tierName: tierNames[user.tier] || user.tier,
   720      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   721      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   722      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   723      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   724      overageHours,
   725      overageCharge
   726    };
   727  } catch (err) {
   728    await client.query('ROLLBACK');
   729    throw err;
   730  } finally {
   731    client.release();
   732  }
   733}
