    console.log(`[UsageTracking] Released ${reservedHours.toFixed(4)}hrs for ${stripeCustomerId} (${reason})`);

    // Build balance from RETURNING data (no extra SELECT needed)
    const hoursTotal = parseFloat(user.hours_total);
