    log("QE DOM IS NOT ACCESSIBLE FROM UXP", "error");
    log("You will need to use the manual split workaround.", "warning");
    log(`\nMethods found: ${methodsFound}`, "dim");
    log(`Methods working: ${methodsWorking}`, "dim");
  }
}

