    const wsUrl = `ws://127.0.0.1:${this.port}`;

    return new Promise((resolve, reject) => {
      this.ws = new WebSocket(wsUrl);

      this.ws.onopen = () => resolve(this.ws);
      this.ws.onerror = (err) => reject(new Error('Connection failed'));

