     1→/**
     2→ * Slice 9: XML-Based Razor (Custom Razor API)
     3→ *
     4→ * Implements actual clip splitting via FCP XML export/modify/import workflow.
     5→ * This is the proven approach used by commercial tools like TimeBolt and Recut.
     6→ *
     7→ * Workflow:
     8→ * 1. Export sequence to FCP XML (via Premiere's built-in export)
     9→ * 2. Detect silences using FFprobe (actual audio analysis)
    10→ * 3. Send XML + silences to backend for processing
    11→ * 4. Import processed XML as new sequence
    12→ */
    13→
    14→// Store current FFprobe silences (separate from Whisper-based silences)
    15→let currentAudioSilences = [];
    16→let xmlExportPath = '/tmp/splice_export.xml';
    17→
    18→/**
    19→ * Initialize Slice 9 - Razor functionality
    20→ */
    21→function initSlice9() {
    22→  initFFprobeSilenceDetection();
    23→  initRazorButton();
    24→  initExportXMLButton();
    25→  checkFFprobeAvailability();
    26→}
    27→
    28→/**
    29→ * Check if FFprobe is available on the system
    30→ */
    31→async function checkFFprobeAvailability() {
    32→  try {
    33→    const response = await fetch(`${BACKEND_URL}/ffprobe-check`);
    34→    const data = await response.json();
    35→
    36→    if (!data.installed) {
    37→      console.warn('[SPLICE] FFprobe not installed:', data.message);
    38→      // Show warning in UI
    39→      const razorSection = document.getElementById('razorSection');
    40→      if (razorSection) {
    41→        const warning = document.createElement('p');
    42→        warning.style.cssText = 'color: #ff6b6b; font-size: 10px; margin: 5px 0;';
    43→        warning.textContent = 'FFprobe not found. Install: brew install ffmpeg';
    44→        razorSection.prepend(warning);
    45→      }
    46→    } else {
    47→      console.log('[SPLICE] FFprobe available');
    48→    }
    49→  } catch (err) {
    50→    console.warn('[SPLICE] Could not check FFprobe status:', err.message);
    51→  }
    52→}
    53→
    54→/**
    55→ * Initialize FFprobe-based silence detection button
    56→ */
    57→function initFFprobeSilenceDetection() {
    58→  const btn = document.getElementById('detectAudioSilencesBtn');
    59→  if (!btn) return;
    60→
    61→  btn.addEventListener('click', async () => {
    62→    btn.disabled = true;
    63→    setStatus('Detecting silences (FFprobe)...');
    64→
