     1→/**
     2→ * SPLICE Backend Server
     3→ *
     4→ * Main entry point for the SPLICE backend API.
     5→ * Orchestrates the audio analysis pipeline.
     6→ *
     7→ * Slices:
     8→ * - Slice 4: Transcription (services/transcription.js)
     9→ * - Slice 5: Take Detection (services/takeDetection.js)
    10→ */
    11→
    12→require('dotenv').config();
    13→
    14→const express = require('express');
    15→const cors = require('cors');
    16→const fs = require('fs');
    17→const https = require('https');
    18→const path = require('path');
    19→
    20→// Import slice services
    21→const { transcribeAudio } = require('./services/transcription');
    22→const { detectTakes } = require('./services/takeDetection');
    23→const { detectSilences } = require('./services/silenceDetection');
    24→const { detectAudioSilences, isFFprobeInstalled } = require('./services/ffprobeSilence');
    25→const { processXMLFile } = require('./services/xmlProcessor');
    26→
    27→// =============================================================================
    28→// Server Configuration
    29→// =============================================================================
    30→
    31→const app = express();
    32→const PORT = process.env.PORT || 3847;
    33→
    34→// HTTPS certificates (generated by mkcert)
    35→const httpsOptions = {
    36→  key: fs.readFileSync(path.join(__dirname, 'localhost+1-key.pem')),
    37→  cert: fs.readFileSync(path.join(__dirname, 'localhost+1.pem'))
    38→};
    39→
    40→app.use(cors());
    41→app.use(express.json());
    42→
    43→// =============================================================================
    44→// Routes
    45→// =============================================================================
    46→
    47→/**
    48→ * GET / - API information
    49→ */
    50→app.get('/', (req, res) => {
    51→  res.json({
    52→    service: 'splice-backend',
    53→    version: '0.2.0',
    54→    endpoints: {
    55→      'GET /': 'This info',
    56→      'GET /health': 'Health check',
    57→      'GET /ffprobe-check': 'Check if FFprobe is installed',
    58→      'POST /analyze': 'Analyze WAV file { wavPath }',
    59→      'POST /silences': 'Detect silences via Whisper gaps { wavPath, threshold: 0.5 }',
    60→      'POST /silences-audio': 'Detect silences via FFprobe { wavPath, threshold: -30, minDuration: 0.5, padding: 0.1 }',
    61→      'POST /process-xml': 'Process FCP XML { xmlPath, silences, removeGaps: true }'
    62→    }
    63→  });
    64→});
    65→
    66→/**
    67→ * GET /health - Health check
    68→ */
    69→app.get('/health', (req, res) => {
    70→  res.json({ status: 'ok', service: 'splice-backend' });
    71→});
    72→
    73→/**
    74→ * POST /analyze - Main analysis endpoint
    75→ *
    76→ * Pipeline:
    77→ * 1. Validate input (wavPath)
    78→ * 2. Slice 4: Transcribe audio with Groq Whisper
    79→ * 3. Slice 5: Detect takes with GPT-4o-mini
    80→ * 4. Return combined results
    81→ */
    82→app.post('/analyze', async (req, res) => {
    83→  const { wavPath } = req.body;
    84→
    85→  // Validate input
    86→  if (!wavPath) {
    87→    return res.status(400).json({ error: 'wavPath is required' });
    88→  }
    89→
    90→  if (!fs.existsSync(wavPath)) {
    91→    return res.status(404).json({ error: `File not found: ${wavPath}` });
    92→  }
    93→
    94→  console.log(`[SPLICE] Analyzing: ${wavPath}`);
    95→
    96→  try {
    97→    // Slice 4 - Groq Whisper transcription
    98→    const transcript = await transcribeAudio(wavPath);
    99→
   100→    // Slice 5 - GPT-4o-mini take detection
   101→    const takes = await detectTakes(transcript);
   102→
   103→    res.json({
   104→      success: true,
   105→      wavPath,
   106→      transcript,
   107→      takes
   108→    });
   109→  } catch (err) {
   110→    console.error('[SPLICE] Error:', err);
   111→    res.status(500).json({ error: err.message });
   112→  }
   113→});
   114→
   115→/**
   116→ * POST /silences - Detect silent gaps in audio
   117→ *
   118→ * Pipeline:
   119→ * 1. Transcribe audio with Whisper (reuses transcription)
   120→ * 2. Analyze gaps between segments
   121→ * 3. Return silence regions
   122→ */
   123→app.post('/silences', async (req, res) => {
   124→  const { wavPath, threshold = 0.5 } = req.body;
   125→
   126→  if (!wavPath) {
   127→    return res.status(400).json({ error: 'wavPath is required' });
   128→  }
   129→
   130→  if (!fs.existsSync(wavPath)) {
   131→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   132→  }
   133→
   134→  console.log(`[SPLICE] Detecting silences: ${wavPath} (threshold: ${threshold}s)`);
   135→
   136→  try {
   137→    const transcript = await transcribeAudio(wavPath);
   138→    const silences = detectSilences(transcript.segments, threshold);
   139→
   140→    res.json({
   141→      success: true,
   142→      wavPath,
   143→      threshold,
   144→      silences,
   145→      count: silences.length,
   146→      totalSilenceDuration: silences.reduce((sum, s) => sum + s.duration, 0).toFixed(2)
   147→    });
   148→  } catch (err) {
   149→    console.error('[SPLICE] Silence detection error:', err);
   150→    res.status(500).json({ error: err.message });
   151→  }
   152→});
   153→
   154→/**
   155→ * POST /silences-audio - Detect silences using FFprobe audio analysis
   156→ *
   157→ * Uses actual audio levels (dB threshold) instead of transcript gaps.
   158→ * More accurate for detecting silence vs background noise.
   159→ */
   160→app.post('/silences-audio', async (req, res) => {
   161→  const {
   162→    wavPath,
   163→    threshold = -30,
   164→    minDuration = 0.5,
   165→    padding = 0.1
   166→  } = req.body;
   167→
   168→  if (!wavPath) {
   169→    return res.status(400).json({ error: 'wavPath is required' });
   170→  }
   171→
   172→  if (!fs.existsSync(wavPath)) {
   173→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   174→  }
   175→
   176→  // Check FFprobe availability
   177→  const ffprobeAvailable = await isFFprobeInstalled();
   178→  if (!ffprobeAvailable) {
   179→    return res.status(500).json({
   180→      error: 'FFprobe not installed. Run: brew install ffmpeg'
   181→    });
   182→  }
   183→
   184→  console.log(`[SPLICE] FFprobe silence detection: ${wavPath} (threshold: ${threshold}dB, min: ${minDuration}s)`);
