   188→      threshold,
   189→      minDuration,
   190→      padding
   191→    });
   192→
   193→    const totalDuration = silences.reduce((sum, s) => sum + s.duration, 0);
   194→
   195→    res.json({
   196→      success: true,
   197→      wavPath,
   198→      threshold,
   199→      minDuration,
   200→      padding,
   201→      silences,
   202→      count: silences.length,
   203→      totalSilenceDuration: totalDuration.toFixed(2)
   204→    });
   205→  } catch (err) {
   206→    console.error('[SPLICE] FFprobe silence detection error:', err);
   207→    res.status(500).json({ error: err.message });
   208→  }
   209→});
   210→
   211→/**
   212→ * POST /process-xml - Process FCP XML to split clips at silences
   213→ *
   214→ * Takes an FCP XML file and silence timestamps, splits clips
   215→ * at silence boundaries, and optionally removes gaps.
   216→ */
   217→app.post('/process-xml', async (req, res) => {
   218→  const {
   219→    xmlPath,
   220→    silences,
   221→    removeGaps = true,
   222→    outputPath = null
   223→  } = req.body;
   224→
   225→  if (!xmlPath) {
   226→    return res.status(400).json({ error: 'xmlPath is required' });
   227→  }
   228→
   229→  if (!silences || !Array.isArray(silences)) {
   230→    return res.status(400).json({ error: 'silences array is required' });
   231→  }
   232→
   233→  if (!fs.existsSync(xmlPath)) {
   234→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   235→  }
   236→
   237→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   238→
   239→  try {
   240→    const result = await processXMLFile(xmlPath, silences, {
   241→      outputPath,
   242→      removeGaps
   243→    });
   244→
   245→    res.json({
   246→      success: true,
   247→      inputPath: xmlPath,
   248→      outputPath: result.outputPath,
   249→      stats: result.stats
   250→    });
   251→  } catch (err) {
   252→    console.error('[SPLICE] XML processing error:', err);
   253→    res.status(500).json({ error: err.message });
   254→  }
   255→});
   256→
   257→/**
   258→ * GET /ffprobe-check - Check if FFprobe is installed
   259→ */
   260→app.get('/ffprobe-check', async (req, res) => {
   261→  const installed = await isFFprobeInstalled();
   262→  res.json({
