import { spawn } from "child_process";

export const MAX_CODE_SIZE = 100 * 1024; // 100KB limit
export const TIMEOUT_MS = 120 * 1000; // 2 minute timeout

export function runClaudeCommand(prompt: string, input: string, timeout: number): Promise<string> {
  return new Promise((resolve, reject) => {
    const child = spawn("claude", ["-p", prompt], {
      stdio: ["pipe", "pipe", "pipe"],
    });

    let stdout = "";
    let stderr = "";
    let settled = false;

    const settle = <T>(fn: (value: T) => void, value: T) => {
      if (settled) return;
      settled = true;
      clearTimeout(timeoutId);
      fn(value);
    };

    const timeoutId = setTimeout(() => {
      child.kill("SIGTERM");
      settle(reject, new Error("Request timed out - code may be too complex"));
    }, timeout);

    child.stdout.on("data", (data) => {
      stdout += data.toString();
    });

    child.stderr.on("data", (data) => {
      stderr += data.toString();
    });

    child.on("close", (exitCode) => {
      if (exitCode === 0) {
        settle(resolve, stdout.trim());
      } else {
        settle(reject, new Error(stderr || `Process exited with code ${exitCode}`));
      }
    });

    child.on("error", (err) => {
      settle(reject, err);
    });

    child.stdin.write(input);
    child.stdin.end();
  });
}

export const BEAUTIFY_PROMPT = `You are a JavaScript code beautifier. Your ONLY job is to output beautified code.

RULES:
1. Output ONLY valid JavaScript code - no markdown, no explanations, no questions
2. Rename obfuscated variables (a, b, _0x123) to meaningful names
3. Add brief comments for complex logic
4. Preserve all functionality

CRITICAL: Your response must start with code and contain ONLY code. Never ask questions or add explanations.`;

const VARIABLE_MAPPING_FORMAT = (minified: string, original: string) => `- ${minified} → ${original}`;

/**
 * Get the beautify prompt with optional source map variable hints
 * @param variableHints - Optional mapping of minified names to original names from source map
 * @returns The beautify prompt, optionally augmented with variable mappings
 */
export function getBeautifyPrompt(variableHints?: Record<string, string>): string {
  if (!variableHints || Object.keys(variableHints).length === 0) {
    return BEAUTIFY_PROMPT;
  }

  const mappingLines = Object.entries(variableHints)
    .map(([minified, original]) => VARIABLE_MAPPING_FORMAT(minified, original))
    .join('\n');

  return `${BEAUTIFY_PROMPT}

VARIABLE MAPPING (from source map):
The following variables have known original names. Use these exact names:
${mappingLines}`;
}

export const EXPLANATION_PROMPT = `You are a code analyst helping developers understand JavaScript code.

Analyze the provided code and explain:

## Obfuscation Techniques
Identify any obfuscation patterns used:
- Hex strings (\\x48\\x65\\x6c\\x6c\\x6f)
- Base64 encoding (atob('...'))
- Array string lookups (_0x1234[0])
- Eval wrappers (eval(...))
- Other techniques

## What This Code Does
Explain the code's purpose and logic in plain language. Be concise but thorough.

## Key Patterns
Highlight useful patterns, idioms, or techniques that developers could learn from or reuse.

## Notes
Any security concerns, potential issues, or important observations.

FORMAT: Use markdown with headers. Keep explanations practical and educational.`;

