// Maximum number of chunks that can be queued for word-level timestamp API processing
const MAX_FILLER_WORDS_UPGRADE_QUEUE_SIZE = 2;

/**
 * Upgrades filler word detection accuracy by sending audio chunks to word-level timestamps API
 */
async function upgradeFillerWordsAccuracyIfRequired() {
    console.log('filler_words_upgrade_accuracy_if_required');

    for (let chunkIndex = 0; chunkIndex < g_filler_words_chunks_status.length; chunkIndex++) {
        if (g_filler_words_chunks_status[chunkIndex] === 'queued for sending to word level timestamps API') {
            console.log('chunk ' + chunkIndex + ' is queued');

            // Disable UI buttons and show spinner
            $('#button-remove-filler-words-markers').prop('disabled', true);
            $('#button-remove-filler-words-go').prop('disabled', true);
            $('#filler-words-processing-spinner').removeClass('hidden');

            // Clone current transcript for word-level processing
            g_filler_words_transcript_word_level[chunkIndex] = JSON.parse(
                JSON.stringify(g_filler_words_transcript[chunkIndex])
            );

            // Check if queue is full
            const queuedCount = g_filler_words_chunks_status.reduce(
                (count, status) => count + (status === 'sent to word level timestamps API'),
                0
            );

            if (queuedCount >= MAX_FILLER_WORDS_UPGRADE_QUEUE_SIZE) {
                console.log('queue is full');
                continue;
            }

            console.log('sending');

            try {
                g_filler_words_chunks_status[chunkIndex] = 'sent to word level timestamps API';

                const audioFilePath = path.join(DIR_temporary_files, 'filler_words', chunkIndex + '_concat.mp3');
                const transcript = g_filler_words_transcript[chunkIndex];

                const whisperResponse = await test_67_improve_accuracy_words_only_chunked(
                    audioFilePath,
                    transcript,
                    0,
                    'en',
                    transcript.segments.map(segment => segment.id)
                );

                const chunkStartOffset = g_filler_words_chunks[chunkIndex][0];
                console.log('whisper_response_for_chunk', clone(whisperResponse));

                // Process each segment from the whisper response
                for (let segmentIndex = 0; segmentIndex < whisperResponse.segments.length; segmentIndex++) {
                    const segment = whisperResponse.segments[segmentIndex];
                    segment.word_level_accuracy = true;
                    segment.text = segment.text.trim();
                    g_filler_words_transcript_word_level[chunkIndex].segments[segmentIndex] = segment;

                    // Adjust word timestamps relative to chunk position
                    for (let wordIndex = 0; wordIndex < segment.words.length; wordIndex++) {
                        const word = segment.words[wordIndex];

                        // Adjust start/end times relative to chunk offset and filler prompt duration
                        word.start = Math.max(
                            (word.start + chunkStartOffset) - filler_prompt_duration,
                            0
                        );
                        word.end = Math.max(
                            (word.end + chunkStartOffset) - filler_prompt_duration,
                            0
                        );

                        // Handle edge case where start equals end
                        if (word.start === word.end) {
                            if (wordIndex > 0) {
                                const prevWordEnd = whisperResponse.segments[segmentIndex].words[wordIndex - 1].end;
                                word.start -= (word.start - prevWordEnd) / 2;
                            }
                            if (wordIndex < whisperResponse.segments[segmentIndex].words.length - 1) {
                                const nextWordStart = whisperResponse.segments[segmentIndex].words[wordIndex + 1].start;
                                word.end += (nextWordStart - word.end) / 2;
                            }
                        }

                        word.word = word.word.trim();
                        word.text = word.word;
                        g_filler_words_transcript_word_level[chunkIndex].segments[segmentIndex].words[wordIndex] = word;
                    }
                }

                g_filler_words_chunks_status[chunkIndex] = 'upgraded to word level timestamps';

                // Re-enable UI
                $('#button-remove-filler-words-markers').prop('disabled', false);
                $('#button-remove-filler-words-go').prop('disabled', false);
                $('#filler-words-processing-spinner').addClass('hidden');

                // Continue processing and update UI
                upgradeFillerWordsAccuracyIfRequired();
                writeHTML_filler_words_results();
                adjust_filler_words_timestamps();

            } catch (error) {
                g_filler_words_chunks_status[chunkIndex] = 'error';
            }
        }
    }
}

/**
 * Cleans a word by removing non-alphanumeric characters and converting to lowercase
 */
function cleanFillerWord(word) {
    return word.replace(/[^a-zA-Z0-9]/g, '').toLowerCase();
}

/**
 * Resets filler words state to defaults
 */
function resetFillerWords() {
    g_filler_words = clone(g_filler_words_default);
    g_filler_words_extended = clone(g_filler_words_default)
        .concat(filler_word_prompt.split(', '))
        .filter(word => word !== ' ' && word !== '');
    g_filler_words_in = null;
    g_filler_words_out = null;
    g_filler_words_tags_selected = {};
    g_filler_words_user_selected = {};
    renderRemoveSilencesWordTags(g_filler_words);
}

/**
 * Gets word at specified position in transcript
 */
function getWordAtTranscriptPosition(chunkIndex, segmentIndex, wordIndex) {
    if (!g_filler_words_transcript[chunkIndex]) return null;
    if (!g_filler_words_transcript[chunkIndex].segments[segmentIndex]) return null;
    if (!g_filler_words_transcript[chunkIndex].segments[segmentIndex].words[wordIndex]) return null;

    const wordData = g_filler_words_transcript[chunkIndex].segments[segmentIndex].words[wordIndex];
    let cleanedWord;

    if (typeof wordData === 'object') {
        cleanedWord = cleanFillerWord(engine === 'web' ? wordData.text : wordData.word);
    } else {
        cleanedWord = cleanFillerWord(wordData);
    }

    return {
        chunk_index: chunkIndex,
        segment_index: segmentIndex,
        word_index: wordIndex,
        word: cleanedWord
    };
}

/**
 * Gets the previous word in the transcript
 */
function getPreviousTranscriptWord(chunkIndex, segmentIndex, wordIndex) {
    wordIndex -= 1;

    if (wordIndex < 0) {
        segmentIndex -= 1;
        if (segmentIndex < 0) {
            chunkIndex -= 1;
            segmentIndex = g_filler_words_transcript[chunkIndex].segments.length - 1;
        }
        wordIndex = g_filler_words_transcript[chunkIndex].segments[segmentIndex].words.length - 1;
    }

    return getWordAtTranscriptPosition(chunkIndex, segmentIndex, wordIndex);
}

/**
 * Gets the next word in the transcript
 */
function getNextTranscriptWord(chunkIndex, segmentIndex, wordIndex) {
    // Try next word in same segment
    let nextWord = getWordAtTranscriptPosition(chunkIndex, segmentIndex, wordIndex + 1);
    if (nextWord) return nextWord;

    // Try first word of next segment
    nextWord = getWordAtTranscriptPosition(chunkIndex, segmentIndex + 1, 0);
    if (nextWord) return nextWord;

    // Try first word of next chunk
    nextWord = getWordAtTranscriptPosition(chunkIndex + 1, 0, 0);
    if (nextWord) return nextWord;

    return null;
}

/**
 * Checks if word at position matches filler word tags
 */
function isFillerWordAtPositionByTags(chunkIndex, segmentIndex, wordIndex) {
    const wordData = getWordAtTranscriptPosition(chunkIndex, segmentIndex, wordIndex);
    if (wordData === null) return false;

    const cleanedWord = cleanFillerWord(wordData.word);

    for (let fillerWord of g_filler_words) {
        const fillerParts = fillerWord.split(' ').map(part => cleanFillerWord(part));

        // Single word filler
        if (fillerParts.length === 1 && matchFillerWord(fillerParts[0], cleanedWord)) {
            addWordToFillerState(chunkIndex, segmentIndex, wordIndex, cleanedWord, true, g_filler_words_tags_selected);
            return true;
        }

        // Multi-word filler phrase
        if (fillerParts.length > 1 && fillerParts.includes(cleanedWord)) {
            matchLoop:
            for (let partIndex = 0; partIndex < fillerParts.length; partIndex++) {
                if (matchFillerWord(fillerParts[partIndex], cleanedWord)) {
                    // Check previous words match
                    let prevWord = wordData;
                    for (let prevIndex = partIndex - 1; prevIndex >= 0; prevIndex--) {
                        prevWord = getPreviousTranscriptWord(
                            prevWord.chunk_index,
                            prevWord.segment_index,
                            prevWord.word_index
                        );
                        if (!prevWord || prevWord.word !== fillerParts[prevIndex]) {
                            break matchLoop;
                        }
                    }

                    // Check next words match
                    let nextWord = wordData;
                    for (let nextIndex = partIndex + 1; nextIndex < fillerParts.length; nextIndex++) {
                        nextWord = getNextTranscriptWord(
                            nextWord.chunk_index,
                            nextWord.segment_index,
                            nextWord.word_index
                        );
                        if (!prevWord || nextWord.word !== fillerParts[nextIndex]) {
                            break matchLoop;
                        }
                    }

                    // Check if not already in user selected state
                    if (getFillerWordAtPosition(chunkIndex, segmentIndex, wordIndex, g_filler_words_user_selected) != null) {
                        addWordToFillerState(chunkIndex, segmentIndex, wordIndex, cleanedWord, true, g_filler_words_user_selected);
                    }
                    addWordToFillerState(chunkIndex, segmentIndex, wordIndex, cleanedWord, true, g_filler_words_tags_selected);
                    return true;
                }
            }
        }
    }

    // Check if already marked in tags
    return getFillerWordAtPosition(chunkIndex, segmentIndex, wordIndex, g_filler_words_tags_selected) === true
        ? true
        : null;
}

/**
 * Matches a filler word against a target word, handling similar sounds (um/uh/ah variants)
 */
function matchFillerWord(fillerWord, targetWord) {
    const umVariants = g_filler_words.filter(word => filler_word_prompt_um.includes(word));
    const uhVariants = g_filler_words.filter(word => filler_word_prompt_uh.includes(word));
    const ahVariants = g_filler_words.filter(word => filler_word_prompt_ah.includes(word));

    if (umVariants.length > 0 && filler_word_prompt_um.includes(targetWord)) {
        return true;
    }
    if (uhVariants.length > 0 && filler_word_prompt_uh.includes(targetWord)) {
        return true;
    }
    if (ahVariants.length > 0 && filler_word_prompt_ah.includes(targetWord)) {
        return true;
    }

    return fillerWord === targetWord;
}

/**
 * Gets filler word state at a specific position
 */
function getFillerWordAtPosition(chunkIndex, segmentIndex, wordIndex, stateObject) {
    if (stateObject[chunkIndex] && 
        stateObject[chunkIndex][segmentIndex] && 
        stateObject[chunkIndex][segmentIndex][wordIndex]) {
        return stateObject[chunkIndex][segmentIndex][wordIndex].remove;
    }
    return null;
}

/**
 * Adds a word to the filler words state
 */
function addWordToFillerState(chunkIndex, segmentIndex, wordIndex, word, shouldRemove, stateObject) {
    if (!stateObject[chunkIndex]) {
        stateObject[chunkIndex] = {};
    }
    if (!stateObject[chunkIndex][segmentIndex]) {
        stateObject[chunkIndex][segmentIndex] = {};
    }
    stateObject[chunkIndex][segmentIndex][wordIndex] = {
        remove: shouldRemove,
        word: word
    };
}

/**
 * Checks if word at transcript position is marked as filler
 */
function isWordFillerAtTranscriptPosition(chunkIndex, segmentIndex, wordIndex) {
    let isFillerByTags = isFillerWordAtPositionByTags(chunkIndex, segmentIndex, wordIndex);
    const userSelection = getFillerWordAtPosition(chunkIndex, segmentIndex, wordIndex, g_filler_words_user_selected);

    if (userSelection !== null) {
        isFillerByTags = userSelection;
    }

    return isFillerByTags;
}

/**
 * Updates filler word removal state for words matching a pattern
 */
function updateFillerWordRemovalStates(pattern, shouldRemove, stateObject) {
    if (!pattern) return;

    const words = pattern.split(' ');

    for (let word of words) {
        word = cleanFillerWord(word);

        for (let chunkIndex in stateObject) {
            const chunk = stateObject[chunkIndex];
            for (let segmentIndex in chunk) {
                const segment = chunk[segmentIndex];
                for (let wordIndex in segment) {
                    const wordState = segment[wordIndex];
                    if (matchFillerWord(wordState.word, word)) {
                        wordState.remove = shouldRemove;
                    }
                }
            }
        }
    }
}

/**
 * Gets timing ranges for all filler words to be removed
 */
function getFillerWordsTimings(mergeConsecutive) {
    // Merge tag and user selections
    const mergedState = {};

    for (let chunkIndex in g_filler_words_tags_selected) {
        const chunk = g_filler_words_tags_selected[chunkIndex];
        for (let segmentIndex in chunk) {
            const segment = chunk[segmentIndex];
            for (let wordIndex in segment) {
                const wordState = segment[wordIndex];
                addWordToFillerState(chunkIndex, segmentIndex, wordIndex, wordState.word, wordState.remove, mergedState);
            }
        }
    }

    // User selections override tag selections
    for (let chunkIndex in g_filler_words_user_selected) {
        const chunk = g_filler_words_user_selected[chunkIndex];
        for (let segmentIndex in chunk) {
            const segment = chunk[segmentIndex];
            for (let wordIndex in segment) {
                const wordState = segment[wordIndex];
                addWordToFillerState(chunkIndex, segmentIndex, wordIndex, wordState.word, wordState.remove, mergedState);
            }
        }
    }

    const timingRanges = [];
    let currentRange = [];
    let previousWasFiller = false;

    for (let chunkIndex = 0; chunkIndex < g_filler_words_transcript_word_level_adjusted.length; chunkIndex++) {
        const chunk = g_filler_words_transcript_word_level_adjusted[chunkIndex];
        if (!chunk) continue;

        for (let segmentIndex = 0; segmentIndex < chunk.segments.length; segmentIndex++) {
            const segment = chunk.segments[segmentIndex];

            for (let wordIndex = 0; wordIndex < segment.words.length; wordIndex++) {
                const word = segment.words[wordIndex];
                const isFillerWord = getFillerWordAtPosition(chunkIndex, segmentIndex, wordIndex, mergedState);

                if (!mergeConsecutive) {
                    // Non-merging mode: each filler word gets its own timing
                    if (isFillerWord) {
                        const timing = [word.start, word.end];
                        addWordTimingToSelection(timing, timingRanges);
                    }
                } else {
                    // Merging mode: consecutive filler words are combined
                    if (isFillerWord && currentRange.length === 0) {
                        currentRange = [word.start, word.end];
                    } else if (isFillerWord && previousWasFiller) {
                        currentRange[1] = word.end;
                    } else {
                        addWordTimingToSelection(currentRange, timingRanges);
                        currentRange = [];
                    }
                    previousWasFiller = isFillerWord;
                }
            }
        }
    }

    return timingRanges;
}

/**
 * Adds a word timing to the selection if valid
 */
function addWordTimingToSelection(timing, timingRanges) {
    if (timing[1] > timing[0]) {
        console.log('Filler word timing was valid, adding to filler_words_selection');
        timingRanges.push(clone(timing));
    } else {
        console.log('Filler word timing was invalid, not adding to filler_words_selection');
    }
}

/**
 * Selects a filler word in the transcript and positions the player
 */
function selectTranscriptFillerWord(
    chunkIndex = null,
    segmentIndex = null,
    wordIndex = null,
    unused1 = null,
    unused2 = null,
    startPadding = 0,
    endPadding = 0
) {
    if (chunkIndex == null || segmentIndex == null || wordIndex == null) {
        g_selected_transcript_word_level_filler = null;
        return;
    }

    // Check if word-level data is available, queue for upgrade if needed
    if (g_filler_words_transcript_word_level_adjusted[chunkIndex] == null) {
        if (g_filler_words_chunks_status[chunkIndex] === 'received from openai') {
            g_filler_words_chunks_status[chunkIndex] = 'queued for sending to word level timestamps API';
            upgradeFillerWordsAccuracyIfRequired();
        }
        return;
    }

    g_selected_transcript_word_level_filler = {
        chunk_index: chunkIndex,
        segment_index: segmentIndex,
        word_index: wordIndex
    };

    const wordStart = g_filler_words_transcript_word_level_adjusted[chunkIndex].segments[segmentIndex].words[wordIndex].start;
    const wordEnd = g_filler_words_transcript_word_level_adjusted[chunkIndex].segments[segmentIndex].words[wordIndex].end;

    let inPoint = wordStart + startPadding;
    let outPoint = wordEnd - endPadding;
    let shouldSetPoints = true;

    // Ensure minimum duration of 0.1 seconds
    if (inPoint > outPoint - 0.1) {
        shouldSetPoints = false;
    }

    console.log('Moving to time section 2: ', inPoint, outPoint);

    if (shouldSetPoints) {
        jsxProject.setInPoint(inPoint);
        jsxProject.setOutPoint(outPoint);
    }
    jsxProject.setPlayerPosition(inPoint);
}

/**
 * Removes invalid segments (null or zero/negative duration)
 */
function removeBadSegments(transcript) {
    for (let i = 0; i < transcript.segments.length; i++) {
        const segment = transcript.segments[i];
        if (segment.start == null || segment.end == null || segment.end <= segment.start) {
            transcript.segments.splice(i, 1);
            i--;
        }
    }
    return transcript;
}

/**
 * Checks if no filler words were found and shows message
 */
function checkNoFillerWords() {
    let noFillerWordsFound = true;

    for (let i = 0; i < g_filler_words_chunks_status.length; i++) {
        for (let j = 0; j < g_filler_words_chunks_status[i].length; j++) {
            if (g_filler_words_chunks_status[i][j] != 'no filler words in segment') {
                noFillerWordsFound = false;
                break;
            }
        }
    }

    if (noFillerWordsFound) {
        showMessage(
            "Sorry, no filler words found. If there are definitely filler words in your audio, " +
            "please make sure you've followed the 'best practices' in the previous menu. " +
            "If still no luck, let us know in the 'share feedback' menu and we'll continue to improve detection!"
        );
        open_menu('remove-filler-words');
    }
}

/**
 * Combines multiple segments into one
 */
function combineSegments(segments) {
    console.log('combine_segments called with segments: ', JSON.stringify(segments));

    const combined = {
        start: segments[0].start,
        end: segments[segments.length - 1].end,
        words: []
    };

    for (let i = 0; i < segments.length; i++) {
        for (let j = 0; j < segments[i].words.length; j++) {
            combined.words.push(JSON.parse(JSON.stringify(segments[i].words[j])));
        }
    }

    console.log('combine_segments returning combined_segment: ', JSON.stringify(combined));
    return combined;
}

/**
 * Transcribes audio file for filler word detection
 */
async function transcribeAudioFileForFillerWords(audioFilePath, language = 'en') {
    regular_license_key_checker();

    const fileStream = fs.createReadStream(audioFilePath);
    const fileStats = fs.statSync(audioFilePath);
