                }
            }
        }
    };

    const options = environmentConfigs[ENVIRONMENT].WHISPER_OPTIONS;
    console.log('options');
    console.log(options);

    // Retry up to 3 times with 2 second delay, 60 second timeout
    return await retryFormDataSubmit(formData, options, 3, 2 * 1000, 60 * 1000);
}

/**
 * Adjusts filler word timestamps by bridging gaps between words
 */
function adjustFillerWordsTimestamps() {
    filler_words_adjustment_params = test_55_get_filler_words_adjustment_params();
    console.log('PARAMS', filler_words_adjustment_params);

    // Clone the word-level transcript for adjustments
    g_filler_words_transcript_word_level_adjusted = JSON.parse(
        JSON.stringify(g_filler_words_transcript_word_level)
    );

    console.log('g_filler_words_transcript_word_level_adjusted');
    console.log(JSON.parse(JSON.stringify(g_filler_words_transcript_word_level_adjusted)));

    for (let chunkIndex = 0; chunkIndex < g_filler_words_transcript_word_level_adjusted.length; chunkIndex++) {
        try {
            const chunk = g_filler_words_transcript_word_level_adjusted[chunkIndex];
            if (chunk == null) continue;

            for (let segmentIndex = 0; segmentIndex < chunk.segments.length; segmentIndex++) {
                try {
                    if (chunk.segments[segmentIndex] == null) continue;

                    for (let wordIndex = 0; wordIndex < chunk.segments[segmentIndex].words.length; wordIndex++) {
                        if (filler_words_adjustment_params.bridging) {
                            try {
                                // Skip first word of first segment of first chunk
                                if (wordIndex === 0) {
                                    if (chunkIndex === 0 && segmentIndex == 0) continue;

                                    // Bridge to previous segment
                                    let prevSegment = null;
                                    if (segmentIndex > 0) {
                                        prevSegment = chunk.segments[segmentIndex - 1];
                                    } else {
                                        if (g_filler_words_transcript_word_level_adjusted[chunkIndex - 1] == null) continue;
                                        const prevChunk = g_filler_words_transcript_word_level_adjusted[chunkIndex - 1];
                                        prevSegment = prevChunk.segments[prevChunk.segments.length - 1];
                                    }

                                    // Set midpoint between previous word end and current word start
                                    const prevWordEnd = prevSegment.words[prevSegment.words.length - 1].end;
                                    const currentWordStart = chunk.segments[segmentIndex].words[wordIndex].start;
                                    const midpoint = (prevWordEnd + currentWordStart) / 2;

                                    prevSegment.words[prevSegment.words.length - 1].end = midpoint;
                                    chunk.segments[segmentIndex].words[wordIndex].start = midpoint;
                                    continue;
                                }
                            } catch (error) {
                                console.log('Error in adjusting filler word timestamps:', error);
                            }

                            // Bridge gap between consecutive words in same segment
                            const prevWordEnd = chunk.segments[segmentIndex].words[wordIndex - 1].end;
                            const currentWordStart = chunk.segments[segmentIndex].words[wordIndex].start;
                            const midpoint = (prevWordEnd + currentWordStart) / 2;

                            chunk.segments[segmentIndex].words[wordIndex - 1].end = midpoint;
                            chunk.segments[segmentIndex].words[wordIndex].start = midpoint;
                        }
                    }
                } catch (error) {
                    console.log('err', error);
                }
            }
        } catch (error) {
            console.log('err', error);
        }
    }

    // Re-select current word if one was selected
    if (g_selected_transcript_word_level_filler != null) {
        selectTranscriptFillerWord(
            g_selected_transcript_word_level_filler.chunk_index,
            g_selected_transcript_word_level_filler.segment_index,
            g_selected_transcript_word_level_filler.word_index
        );
    }
}

