// Maximum number of chunks that can be queued for word-level timestamp API processing
const MAX_FILLER_WORDS_UPGRADE_QUEUE_SIZE = 2;

/**
 * Upgrades filler word detection accuracy by sending audio chunks to word-level timestamps API
 */
async function upgradeFillerWordsAccuracyIfRequired() {
    console.log('filler_words_upgrade_accuracy_if_required');

    for (let chunkIndex = 0; chunkIndex < g_filler_words_chunks_status.length; chunkIndex++) {
        if (g_filler_words_chunks_status[chunkIndex] === 'queued for sending to word level timestamps API') {
            console.log('chunk ' + chunkIndex + ' is queued');

            // Disable UI buttons and show spinner
            $('#button-remove-filler-words-markers').prop('disabled', true);
            $('#button-remove-filler-words-go').prop('disabled', true);
            $('#filler-words-processing-spinner').removeClass('hidden');

            // Clone current transcript for word-level processing
            g_filler_words_transcript_word_level[chunkIndex] = JSON.parse(
                JSON.stringify(g_filler_words_transcript[chunkIndex])
            );

            // Check if queue is full
            const queuedCount = g_filler_words_chunks_status.reduce(
                (count, status) => count + (status === 'sent to word level timestamps API'),
                0
            );

            if (queuedCount >= MAX_FILLER_WORDS_UPGRADE_QUEUE_SIZE) {
                console.log('queue is full');
                continue;
            }

            console.log('sending');

            try {
                g_filler_words_chunks_status[chunkIndex] = 'sent to word level timestamps API';

                const audioFilePath = path.join(DIR_temporary_files, 'filler_words', chunkIndex + '_concat.mp3');
